import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { Link, useHistory } from 'react-router-dom';
import { signInWithEmailAndPassword, signInWithPopup } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { auth, db, googleProvider } from '../firebaseInit';
import Loading from '../components/Loading';
//import { globalIconStyle } from '../assets/GlobalStyles';
import './Login.css';

const Login = () => {
    const history = useHistory();
    const { setIsProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [logInState, setlogInState] = useState({
        loginEmail: '',
        loginPassword: ''
    });

    const handleLoginChange = (e) => {
        setlogInState({
            ...logInState,
            [e.target.name]: e.target.value
        });
    };

    const onLoginSubmit = async (e) => {
        e.preventDefault();
        if (logInState.loginEmail && logInState.loginPassword) {
            setLoading(true);
            await signInWithEmailAndPassword(auth, logInState.loginEmail, logInState.loginPassword).then(async (userResponse) => {
                const finalUser = userResponse.user;
                const docSnap = await getDoc(doc(db, 'providers', finalUser.uid));
                if (docSnap.exists()) {
                    setIsProvider(true);
                    console.log(`isProvider true in login`);
                } else {
                    console.log(`isProvider false in login`);
                }
                alert(`Successfully logged in with finalUser.uid: ${finalUser.uid}`);
                history.push('/');
            }).catch((error) => {
                alert(`in login/firebaseSignInEmail: Error Code ${error.code}: ${error.message}`);
                setlogInState({
                    loginEmail: '',
                    loginPassword: ''
                });
                setLoading(false);
                return;
            });
        } else {
            alert('Please enter the Email and Password.');
            return;
        }
        setlogInState({
            loginEmail: '',
            loginPassword: ''
        });
        setLoading(false);
    };

    const loginWithGoogle = async () => {
        try {
            setLoading(true);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            // Check if the user is a provider
            const docSnap = await getDoc(doc(db, 'providers', user.uid));
            if (docSnap.exists()) {
                setIsProvider(true);
                console.log(`isProvider true in login`);
            } else {
                console.log(`isProvider false in login`);
            }

            alert(`Successfully logged in with Google: ${user.uid}`);
            history.push('/');
        } catch (error) {
            console.error("Google Login Error:", error);
            alert(`Google Login Error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='login-container'>
            <div className='login-card'>
                {loading ? <Loading /> : (
                    <>
                        <h2 className='login-title'>Welcome Back</h2>
                        <form onSubmit={onLoginSubmit} className='login-form'>
                            <div className='input-group'>
                                <input
                                    type='email'
                                    name='loginEmail'
                                    placeholder='Email'
                                    value={logInState.loginEmail}
                                    onChange={handleLoginChange}
                                    required
                                />
                                <input
                                    type='password'
                                    name='loginPassword'
                                    placeholder='Password'
                                    value={logInState.loginPassword}
                                    onChange={handleLoginChange}
                                    required
                                />
                            </div>

                            <div className='button-group'>
                                <button
                                    type='submit'
                                    className='login-btn primary'
                                    disabled={loading}
                                >
                                    {loading ? 'Logging in...' : 'Login'}
                                </button>
                                <button
                                    type='button'
                                    onClick={loginWithGoogle}
                                    className='login-btn google'
                                    disabled={loading}
                                >

                                    Login with Google
                                </button>
                            </div>
                        </form>
                    </>
                )}
            </div>
            <p className='signup-link'>
                Don't have an account? <Link to='/signup'>Sign up</Link>
            </p>
        </div>
    );
};

export default Login;
