import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword, signInWithPopup } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { auth, db, googleProvider } from '../firebaseInit';
import { Person } from '@mui/icons-material';
import Loading from '../components/Loading';
import { globalIconStyle } from '../assets/GlobalStyles';
import './Login.css';
import illustration from '../assets/images/s101.jpg'; // Add an illustration for a better UI

const Login = () => {
    const navigate = useNavigate();
    const { setIsProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [logInState, setLogInState] = useState({
        loginEmail: '',
        loginPassword: ''
    });

    const handleLoginChange = (e) => {
        setLogInState((prev) => ({
            ...prev,
            [e.target.name]: e.target.value
        }));
    };

    const onLoginSubmit = async (e) => {
        e.preventDefault();
        if (!logInState.loginEmail || !logInState.loginPassword) {
            setError('Please enter both email and password.');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const userResponse = await signInWithEmailAndPassword(auth, logInState.loginEmail, logInState.loginPassword);
            const finalUser = userResponse.user;

            // Check if user is a provider
            const docSnap = await getDoc(doc(db, 'providers', finalUser.uid));
            setIsProvider(docSnap.exists());

            console.log(`isProvider: ${docSnap.exists()} in login`);

            alert(`Successfully logged in!`);
            navigate('/');
        } catch (error) {
            setError(`Error: ${error.message}`);
        } finally {
            setLogInState({ loginEmail: '', loginPassword: '' });
            setLoading(false);
        }
    };

    const loginWithGoogle = async () => {
        setLoading(true);
        setError('');

        try {
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            // Check if the user is a provider
            const docSnap = await getDoc(doc(db, 'providers', user.uid));
            setIsProvider(docSnap.exists());

            console.log(`isProvider: ${docSnap.exists()} in Google login`);

            alert(`Successfully logged in with Google!`);
            navigate('/');
        } catch (error) {
            setError(`Google Login Error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            {/* Left Side - Illustration */}
            <div className="login-left">
                <img src={illustration} alt="Login Illustration" />
            </div>

            {/* Right Side - Login Form */}
            <div className="login-right">
                <div className="login-card">
                    {loading ? (
                        <Loading />
                    ) : (
                        <div className="login-content">
                            <div className="circle size100px">
                                <Person style={globalIconStyle} />
                            </div>
                            <form onSubmit={onLoginSubmit} className="login-form">
                                <h2 className="heading-type3">Log in</h2>

                                {error && <p className="error-message">{error}</p>}

                                <label>Email</label>
                                <input
                                    type="email"
                                    name="loginEmail"
                                    placeholder="abc@example.com"
                                    value={logInState.loginEmail}
                                    onChange={handleLoginChange}
                                    required
                                />

                                <label>Password</label>
                                <input
                                    type="password"
                                    name="loginPassword"
                                    value={logInState.loginPassword}
                                    onChange={handleLoginChange}
                                    required
                                />

                                <button type="submit" className="login-button" disabled={loading}>
                                    Log in with Email
                                </button>

                                <button onClick={loginWithGoogle} className="google-login-button" type="button" disabled={loading}>
                                    <img src="/google-icon.png" alt="Google" className="google-icon" />
                                    Log in with Google
                                </button>

                                <p className="forgot-password">
                                    <Link to="/forgot-password" className="blue-link">Forgot Password?</Link>
                                </p>
                            </form>
                        </div>
                    )}
                </div>

                {/* Signup Section */}
                <div className="login-card login-card2">
                    <p>New user? <Link to="/signup" className="blue-link">Create an account</Link> instead.</p>
                </div>
            </div>
        </div>
    );
};

export default Login;
