import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import {
    createUserWithEmailAndPassword,
    signInWithPopup,
    sendEmailVerification
} from 'firebase/auth';
import { auth, googleProvider, db } from '../firebaseInit';
import { doc, setDoc } from 'firebase/firestore';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import { Button } from '@mui/material';
import Loading from '../components/Loading';
import './ProviderSignupForm.css';

const ProviderSignUpForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [providerSignUpState, setProviderSignUpState] = useState({
        name: '',
        companyName: '',
        email: '',
        phoneNumber: '',
        description: '',
        password: '',
        confirmPassword: ''
    });

    const handleChange = (e) => {
        setProviderSignUpState({
            ...providerSignUpState,
            [e.target.name]: e.target.value
        });
    };

    const handleSignUp = async (e) => {
        e.preventDefault();
        if (providerSignUpState.password !== providerSignUpState.confirmPassword) {
            alert('Passwords do not match!');
            return;
        }
        setLoading(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, providerSignUpState.email, providerSignUpState.password);
            const user = userCredential.user;

            // Send email verification
            await sendEmailVerification(user);
            alert('A verification email has been sent. Please check your inbox and verify your email before logging in.');

            // Store provider data in Firestore with "verified: false"
            await setDoc(doc(db, 'providers', user.uid), {
                name: providerSignUpState.name,
                companyName: providerSignUpState.companyName,
                email: providerSignUpState.email,
                phoneNumber: providerSignUpState.phoneNumber,
                description: providerSignUpState.description,
                role: 1,
                verified: false,
            });

            setProviderSignUpState({
                name: '',
                companyName: '',
                email: '',
                phoneNumber: '',
                description: '',
                password: '',
                confirmPassword: ''
            });
        } catch (error) {
            console.error("Error during sign-up:", error.message);
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    const handleGoogleSignUp = async () => {
        try {
            setLoading(true);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            if (!user.emailVerified) {
                await sendEmailVerification(user);
                alert('A verification email has been sent. Please verify your email before proceeding.');
                return;
            }

            await setDoc(doc(db, 'providers', user.uid), {
                name: user.displayName,
                email: user.email,
                phoneNumber: user.phoneNumber || '',
                companyName: '',
                description: '',
                role: 1,
                verified: true
            }, { merge: true });

            alert('Google Sign-In Successful!');
            history.push('/');
        } catch (error) {
            console.error("Google Sign-In Failed:", error.message);
            alert(`Google Sign-In Failed: ${error.message}`);
        }
        setLoading(false);
    };

    return (
        <div>
            {loading ? <Loading /> : (
                <form className="provider-signup-form" onSubmit={handleSignUp}>
                    <div className="form-header">
                        <h2>Service Provider Registration</h2>
                        <p>Create your professional account</p>
                    </div>

                    <div className="form-section">
                        <h3 className="form-section-title">Business Information</h3>
                        <div className="input-grid">
                            <div className="input-field-provider">
                                <label htmlFor="businessName">Business Name</label>
                                <input
                                    type="text"
                                    id="businessName"
                                    name="companyName"
                                    placeholder="Your business name"
                                    value={providerSignUpState.companyName}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <div className="input-field-provider">
                                <label htmlFor="businessEmail">Business Email</label>
                                <input
                                    type="email"
                                    id="businessEmail"
                                    name="email"
                                    placeholder="business@example.com"
                                    value={providerSignUpState.email}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        </div>
                        <div className="input-field-provider">
                            <label htmlFor="businessAddress">Business Address</label>
                            <textarea
                                id="businessAddress"
                                name="description"
                                placeholder="Enter your complete business address"
                                value={providerSignUpState.description}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    </div>

                    <div className="form-section">
                        <h3 className="form-section-title">Contact Details</h3>
                        <div className="input-grid">
                            <div className="input-field-provider">
                                <label htmlFor="phone">Phone Number</label>
                                <PhoneInput
                                    country={'in'}
                                    value={providerSignUpState.phoneNumber}
                                    onChange={(phone) => setProviderSignUpState({ ...providerSignUpState, phoneNumber: phone })}
                                    required
                                />
                            </div>
                            <div className="input-field-provider">
                                <label htmlFor="altPhone">Alternative Phone</label>
                                <input
                                    type="tel"
                                    id="altPhone"
                                    placeholder="Alternative contact (optional)"
                                />
                            </div>
                        </div>
                    </div>

                    <div className="form-info">
                        Please ensure all business information is accurate and matches your official records.
                    </div>

                    <div className="button-container">
                        <Button
                            type="submit"
                            className="provider-submit-button"
                            variant="contained"
                        >
                            Register as Provider
                        </Button>

                        <div className="divider">
                            <span>OR</span>
                        </div>

                        <Button
                            onClick={handleGoogleSignUp}
                            className="google-button"
                            variant="outlined"
                        >
                            <img
                                src="/google-icon.png"
                                alt='google'
                                style={{ width: '20px', height: '20px' }}
                            />
                            Sign Up with Google
                        </Button>
                    </div>
                </form>
            )}
        </div>
    );
};

export default ProviderSignUpForm;
