import React, { useEffect, useState } from 'react';
import { AccountCircle, MonetizationOn, AddShoppingCart, RequestQuote, Gavel } from '@mui/icons-material';
import { ref, getDownloadURL } from '@firebase/storage';
import { storage } from '../firebaseInit';

const ServiceCard = ({ passedService, passedIndex, showButton, onAddToCart }) => {
    const [imageURL, setImageURL] = useState(null);
    const displayIndex = passedIndex + 1;

    useEffect(() => {
        let isMounted = true;

        if (passedService.serviceImageName && passedService.serviceImageName.trim() !== '') {
            const fetchImageURL = async () => {
                try {
                    // Ensure the path is correctly formatted
                    const imagePath = `serviceImages/${passedService.serviceProvider}/${passedService.serviceImageName}`.trim();
                    console.log('Attempting to fetch image from:', imagePath);

                    const uploadedRef = ref(storage, imagePath);
                    const url = await getDownloadURL(uploadedRef);

                    if (isMounted) {
                        console.log('Image URL retrieved:', url);
                        setImageURL(url);
                    }
                } catch (error) {
                    console.error("Error fetching image:", error.code, error.message);
                    if (isMounted) {
                        setImageURL('/default-placeholder.jpg');
                    }
                }
            };
            fetchImageURL();
        } else {
            setImageURL('/default-placeholder.jpg');
        }

        return () => {
            isMounted = false;
        };
    }, [passedService.serviceImageName, passedService.serviceProvider]);

    const renderPriceSection = () => {
        switch (passedService.pricingType) {
            case 'fixed':
                return (
                    <p className="para-type3 icon-para">
                        <MonetizationOn /> Fixed Price: Rs. {passedService.servicePrice}
                    </p>
                );
            case 'custom':
                return (
                    <button className="button-type3" onClick={() => onRequestQuote(passedService.id)}>
                        <RequestQuote /> Request Custom Quote
                    </button>
                );
            case 'bidding':
                return (
                    <div className="bidding-section">
                        <p className="para-type4">
                            <Gavel /> Current Bids: {passedService.bids?.length || 0}
                        </p>
                        <p className="lowest-bid">
                            Lowest Bid: Rs. {getLowestBid(passedService.bids) || 'No bids yet'}
                        </p>
                    </div>
                );
            default:
                return (
                    <p className="para-type3">Contact for Price</p>
                );
        }
    };

    return (
        <div className="flex-row stretch-justify" key={passedIndex}>
            <div className="service-card">
                <div className="flex-row left-justify">
                    {/* Service Image */}
                    <div className="service-image">
                        {imageURL ? (
                            <img src={imageURL} alt={passedService.serviceTitle} className="final-service-image" />
                        ) : (
                            <p>Loading image...</p>
                        )}
                    </div>

                    {/* Service Details */}
                    <div className="flex-col">
                        <div className="flex-row left-justify">
                            <p className="circle index-circle">{displayIndex}</p>
                            <p className="para-type2">{passedService.serviceTitle}</p>
                            <div className="flex-row left-justify">
                                <AccountCircle />
                                <p>By:</p>
                                <p className="para-type1">{passedService.serviceProvider || "Unknown Provider"}</p>
                            </div>
                        </div>

                        {/* Service Description */}
                        <div className="flex-row left-justify">
                            <p className="grey-container service-description">{passedService.serviceDescription}</p>
                            <div className="flex-max" />
                        </div>

                        {/* Price & Add to Cart */}
                        <div className="flex-row left-justify">
                            {renderPriceSection()}
                            {showButton && passedService.pricingType === 'fixed' && (
                                <button className="button-type1" onClick={() => onAddToCart(passedService.serviceCategory, passedIndex)}>
                                    <AddShoppingCart /> Add To Cart
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ServiceCard;
