import React, { useState, useContext, useEffect } from 'react'
import { useHistory, Link } from 'react-router-dom'
import { AuthContext } from '../contexts/AuthContext'
import { createUserWithEmailAndPassword } from 'firebase/auth'
import { doc, setDoc } from 'firebase/firestore'
import { auth, db } from '../firebaseInit'
import { PersonAdd } from '@mui/icons-material'
import Loading from '../components/Loading'
import { globalIconStyle } from '../assets/GlobalStyles'
import { Switch as MaterialSwitch } from '@mui/material'
import PhoneInput from 'react-phone-input-2'
import 'react-phone-input-2/lib/style.css'

const SignUp = () => {
    const history = useHistory()
    const { isProvider, setIsProvider } = useContext(AuthContext)
    const [loading, setLoading] = useState(false)
    const [showProviderForm, setShowProviderForm] = useState(false)

    useEffect(() => {
        console.log(`isProvider: ${isProvider}`)
    }, [isProvider])

    const handleSwitchCheck = (e) => {
        setShowProviderForm(e.target.checked)
    }

    return (
        <div className='eightyperc-container'>
            <div className='card-type1 signup-card signup-card2'>
                <div className='flex-row'>
                    <p>Sign up as a Service Provider?</p>
                    <MaterialSwitch onChange={handleSwitchCheck} color='primary' checked={showProviderForm} />
                </div>
            </div>
            {showProviderForm ? <ProviderSignUpForm loading={loading} setLoading={setLoading} history={history} /> : <UserSignUpForm loading={loading} setLoading={setLoading} history={history} />}
            <div className='card-type1 signup-card signup-card2'>
                <p>Existing user? <Link to='/login' className='blue-link'>Log in</Link> instead.</p>
            </div>
        </div>
    )
}

export default SignUp
