import React, { useEffect, useState } from 'react';
import { AccountCircle, MonetizationOn, AddShoppingCart, RequestQuote, Gavel } from '@mui/icons-material';
import { ref, getDownloadURL } from '@firebase/storage';
import { storage } from '../firebaseInit';
import { QuoteService } from '../services/QuoteService';
import QuoteRequestModal from './QuoteRequestModal';
import './ServiceCard.css';
import { ELECTRICAL_CATEGORIES, ELECTRICAL_VISITING_CHARGE } from '../models/Service';

const ServiceCard = ({ passedService, passedIndex, showButton, onAddToCart }) => {
    const [imageURL, setImageURL] = useState(null);
    const [imageError, setImageError] = useState(false);
    const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
    const displayIndex = passedIndex + 1;

    useEffect(() => {
        let isMounted = true;

        const fetchImage = async () => {
            if (!passedService.serviceImageName) {
                setImageURL('/images/default-service.jpg');
                return;
            }

            try {
                // Construct the path correctly
                const imagePath = `services/${passedService.serviceId}/${passedService.serviceImageName}`;
                console.log('Fetching image from:', imagePath);

                const imageRef = ref(storage, imagePath);
                const url = await getDownloadURL(imageRef);

                if (isMounted) {
                    console.log('Image URL:', url);
                    setImageURL(url);
                    setImageError(false);
                }
            } catch (error) {
                console.error('Error fetching image:', error);
                if (isMounted) {
                    setImageURL('/images/default-service.jpg');
                    setImageError(true);
                }
            }
        };

        fetchImage();

        return () => {
            isMounted = false;
        };
    }, [passedService.serviceImageName, passedService.serviceId]);

    const isElectricalService = ELECTRICAL_CATEGORIES.includes(passedService.serviceCategory);

    const renderServiceImage = () => (
        <div className="service-image">
            {imageError ? (
                <div className="image-placeholder">
                    <img
                        src="/images/default-service.jpg"
                        alt="Service placeholder"
                        className="default-image"
                    />
                </div>
            ) : imageURL ? (
                <img
                    src={imageURL}
                    alt={passedService.serviceTitle}
                    className="final-service-image"
                    onError={() => setImageError(true)}
                />
            ) : (
                <div className="image-loading">Loading...</div>
            )}
        </div>
    );

    const renderPricingSection = () => {
        switch (passedService.pricingType) {
            case 'fixed':
                return (
                    <div className="pricing-section">
                        <div className="price-details">
                            <p className="para-type3 icon-para">
                                <MonetizationOn /> Service Price: Rs. {passedService.servicePrice}
                            </p>
                            {isElectricalService && (
                                <p className="visiting-charge">
                                    + Rs. {ELECTRICAL_VISITING_CHARGE} visiting charge
                                </p>
                            )}
                            {isElectricalService && (
                                <p className="total-price">
                                    Total: Rs. {Number(passedService.servicePrice) + ELECTRICAL_VISITING_CHARGE}
                                </p>
                            )}
                        </div>
                        {showButton && (
                            <button className="button-type1" onClick={() => onAddToCart(passedService.serviceCategory, passedIndex)}>
                                <AddShoppingCart /> Add To Cart
                            </button>
                        )}
                    </div>
                );
            case 'custom':
                return (
                    <div className="pricing-section">
                        <button className="button-type3" onClick={() => setIsQuoteModalOpen(true)}>
                            <RequestQuote /> Request Custom Quote
                        </button>
                    </div>
                );
            case 'bidding':
                return (
                    <div className="bidding-section">
                        <p className="para-type4">
                            <Gavel /> Current Bids: {passedService.bids?.length || 0}
                        </p>
                        <p className="lowest-bid">
                            Lowest Bid: Rs. {QuoteService.getLowestBid(passedService.bids) || 'No bids yet'}
                        </p>
                    </div>
                );
            default:
                return (
                    <div className="pricing-section">
                        <p className="para-type3">Contact for Price</p>
                    </div>
                );
        }
    };

    return (
        <div className="flex-row stretch-justify" key={passedIndex}>
            <div className="service-card">
                <div className="flex-row left-justify">
                    {renderServiceImage()}
                    <div className="flex-col">
                        <div className="flex-row left-justify">
                            <p className="circle index-circle">{displayIndex}</p>
                            <p className="para-type2">{passedService.serviceTitle}</p>
                            <div className="flex-row left-justify">
                                <AccountCircle />
                                <p>By:</p>
                                <p className="para-type1">{passedService.serviceProvider || "Unknown Provider"}</p>
                            </div>
                        </div>

                        <div className="flex-row left-justify">
                            <p className="grey-container service-description">{passedService.serviceDescription}</p>
                        </div>

                        <div className="flex-row left-justify">
                            {renderPricingSection()}
                        </div>
                    </div>
                </div>
            </div>

            <QuoteRequestModal
                open={isQuoteModalOpen}
                onClose={() => setIsQuoteModalOpen(false)}
                serviceId={passedService.id}
            />
        </div>
    );
};

export default ServiceCard;
