import { db } from '../firebaseInit';
import { collection, addDoc, updateDoc, doc, getDoc } from 'firebase/firestore';

export const QuoteService = {
    async requestCustomQuote(quoteRequest) {
        try {
            const quotesRef = collection(db, 'customQuotes');
            const docRef = await addDoc(quotesRef, {
                ...quoteRequest,
                timestamp: new Date()
            });
            return docRef.id;
        } catch (error) {
            console.error('Error requesting quote:', error);
            throw error;
        }
    },

    async submitBid(serviceId, bid) {
        try {
            const serviceRef = doc(db, 'services', serviceId);
            const serviceDoc = await getDoc(serviceRef);

            if (!serviceDoc.exists()) {
                throw new Error('Service not found');
            }

            const existingBids = serviceDoc.data().bids || [];
            await updateDoc(serviceRef, {
                bids: [...existingBids, bid]
            });

            return true;
        } catch (error) {
            console.error('Error submitting bid:', error);
            throw error;
        }
    },

    getLowestBid(bids) {
        if (!bids || bids.length === 0) return null;
        return Math.min(...bids.map(bid => bid.amount));
    }
};
