import React, { useState } from 'react';
import { AccountCircle, MonetizationOn, AddShoppingCart, RequestQuote, Gavel } from '@mui/icons-material';
import { QuoteService } from '../services/QuoteService';
import QuoteRequestModal from './QuoteRequestModal';
import './ServiceCard.css';
import { ELECTRICAL_CATEGORIES, ELECTRICAL_VISITING_CHARGE } from '../models/Service';

const ServiceCard = ({ passedService, passedIndex, showButton, onAddToCart }) => {
    const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
    const displayIndex = passedIndex + 1;
    const isElectricalService = ELECTRICAL_CATEGORIES.includes(passedService.serviceCategory);

    return (
        <div className="service-card-wrapper" key={passedIndex}>
            <div className="service-card">
                <div className="service-header">
                    <div className="service-title">
                        <span className="index-circle">{displayIndex}</span>
                        <h3>{passedService.serviceTitle}</h3>
                    </div>
                    <div className="price-badge">
                        <MonetizationOn />
                        <span>Rs. {passedService.servicePrice}</span>
                    </div>
                </div>

                <div className="provider-info">
                    <AccountCircle />
                    <span>Provider:</span>
                    <span className="provider-name">{passedService.serviceProvider || "Unknown Provider"}</span>
                </div>

                <div className="service-description">
                    {passedService.serviceDescription}
                </div>

                <div className="service-footer">
                    {isElectricalService && (
                        <div className="visiting-charge-info">
                            <p>+ Rs. {ELECTRICAL_VISITING_CHARGE} visiting charge</p>
                            <p className="total">Total: Rs. {Number(passedService.servicePrice) + ELECTRICAL_VISITING_CHARGE}</p>
                        </div>
                    )}
                    {showButton && (
                        <button className="add-cart-btn" onClick={() => onAddToCart(passedService.serviceCategory, passedIndex)}>
                            <AddShoppingCart /> Add To Cart
                        </button>
                    )}
                </div>
            </div>

            <QuoteRequestModal
                open={isQuoteModalOpen}
                onClose={() => setIsQuoteModalOpen(false)}
                serviceId={passedService.id}
            />
        </div>
    );
};

export default ServiceCard;
