import React, { useContext, useState, useEffect, useCallback } from 'react';
import './Services.css';
import { AuthContext } from '../contexts/AuthContext';
import { doc, collection, query, getDoc, updateDoc, onSnapshot } from 'firebase/firestore';
import { db } from '../firebaseInit';
import Categories from '../components/Categories';
import ServiceCard from '../components/ServiceCard';
import Loading from '../components/Loading';

const serviceCategories = [
    'Cleaning', 'Electricians', 'Plumbers', 'Carpenters', 'Pest Control',
    'Salon for Women', 'Salon for Men', 'Miscellaneous'
];

const Services = () => {
    const { currentUser, isProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);
    const [services, setServices] = useState({});
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        cart: []
    });



    const readUserData = useCallback(async () => {
        if (currentUser) {
            try {
                const docSnap = await getDoc(doc(db, 'users', currentUser.uid));
                if (docSnap.exists()) {
                    const data = docSnap.data();
                    setUserData({
                        name: data.name,
                        email: data.email,
                        cart: data.cart || []
                    });
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        }
    }, [currentUser]);

    useEffect(() => {
        const fetchData = async () => {
            await readUserData();
        };
        fetchData();

        const q = query(collection(db, 'providers'));
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            let allServices = [];

            querySnapshot.forEach((doc) => {
                const providerData = doc.data();

                if (providerData.providerServices && Array.isArray(providerData.providerServices)) {
                    providerData.providerServices.forEach(service => {
                        allServices.push({
                            ...service,
                            providerName: providerData.name || "Unknown Provider",
                            providerImage: providerData.imageUrl || "default-placeholder.jpg"
                        });
                    });
                }
            });

            const categorizedServices = {};
            serviceCategories.forEach(category => {
                categorizedServices[category] = allServices.filter(service => service.serviceCategory === category);
            });

            setServices(categorizedServices);
            setLoading(false);
        }, (error) => {
            console.error("Error fetching services:", error);
            setLoading(false);
        });

        return () => unsubscribe();
    }, [readUserData]);

    const onAddToCart = async (category, index) => {
        setLoading(true);
        if (!currentUser) {
            alert('Please login or sign up first.');
            setLoading(false);
            return;
        }

        const selectedService = services[category][index];
        const updatedCart = [...userData.cart, selectedService];

        try {
            await updateDoc(doc(db, 'users', currentUser.uid), { cart: updatedCart });
            setUserData(prev => ({ ...prev, cart: updatedCart }));
            alert(`Added ${selectedService.serviceTitle} by ${selectedService.providerName} to cart.`);
        } catch (error) {
            console.error('Error adding to cart:', error);
            alert('Failed to add to cart. Please try again.');
        }

        setLoading(false);
    };

    const renderServicesByCategory = () => {
        return Object.entries(services).map(([category, categoryServices]) => {
            // Skip empty categories
            if (!categoryServices || categoryServices.length === 0) return null;

            return (
                <div key={category} className="category-section">
                    <h3 className="category-title">
                        {category.replace(/-/g, ' ').toUpperCase()}
                    </h3>
                    <div className="services-grid">
                        {categoryServices.map((service, index) => (
                            <ServiceCard
                                key={`${category}-${index}`}
                                passedService={service}
                                passedIndex={index}
                                showButton={!isProvider}
                                onAddToCart={onAddToCart}
                            />
                        ))}
                    </div>
                </div>
            );
        }).filter(Boolean); // Remove null entries
    };

    return (
        <div className="services">
            {loading ? (
                <Loading />
            ) : (
                <>
                    <Categories categories={serviceCategories} />
                    <div className="services-content">
                        {renderServicesByCategory()}
                    </div>
                </>
            )}
        </div>
    );
};

export default Services;
