import React, { useState, useContext } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { createUserWithEmailAndPassword, signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { auth, db, googleProvider } from '../firebaseInit';
import Loading from '../components/Loading';
import { Switch as MaterialSwitch } from '@mui/material';

const SignUp = () => {
    const history = useHistory();
    const { isProvider, setIsProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [showProviderForm, setShowProviderForm] = useState(false);
    const [userSignUpState, setUserSignUpState] = useState({ name: '', email: '', password: '' });
    const [providerSignUpState, setProviderSignUpState] = useState({ name: '', companyName: '', email: '', password: '', description: '' });

    const handleSwitchCheck = (e) => setShowProviderForm(e.target.checked);

    const handleGoogleSignIn = async () => {
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;
            alert(`Signed in as: ${user.displayName}`);
            history.push('/');
        } catch (error) {
            alert(`Google Sign-In Error: ${error.message}`);
        }
    };

    const onUserSignUpSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const userResponse = await createUserWithEmailAndPassword(auth, userSignUpState.email, userSignUpState.password);
            await setDoc(doc(db, 'users', userResponse.user.uid), { name: userSignUpState.name, email: userSignUpState.email, role: 0 });
            alert('User account created!');
            history.push('/');
        } catch (error) {
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    const onProviderSignUpSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const providerResponse = await createUserWithEmailAndPassword(auth, providerSignUpState.email, providerSignUpState.password);
            await setDoc(doc(db, 'providers', providerResponse.user.uid), {
                name: providerSignUpState.name,
                companyName: providerSignUpState.companyName,
                email: providerSignUpState.email,
                description: providerSignUpState.description,
                role: 1
            });
            alert('Provider account created!');
            history.push('/');
        } catch (error) {
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    return (
        <div className='signup-container'>
            <div className='signup-toggle'>
                <p>Sign up as a Service Provider?</p>
                <MaterialSwitch onChange={handleSwitchCheck} color='primary' />
            </div>
            {loading ? <Loading /> : showProviderForm ? (
                <form onSubmit={onProviderSignUpSubmit}>
                    <h2>Provider Sign Up</h2>
                    <input type='text' name='name' placeholder='Full Name' onChange={(e) => setProviderSignUpState({ ...providerSignUpState, name: e.target.value })} />
                    <input type='text' name='companyName' placeholder='Company Name' onChange={(e) => setProviderSignUpState({ ...providerSignUpState, companyName: e.target.value })} />
                    <input type='email' name='email' placeholder='Email' onChange={(e) => setProviderSignUpState({ ...providerSignUpState, email: e.target.value })} />
                    <textarea name='description' placeholder='Short Description' onChange={(e) => setProviderSignUpState({ ...providerSignUpState, description: e.target.value })}></textarea>
                    <input type='password' name='password' placeholder='Password' onChange={(e) => setProviderSignUpState({ ...providerSignUpState, password: e.target.value })} />
                    <button type='submit'>Sign Up</button>
                    <button type='button' onClick={handleGoogleSignIn}>Sign Up with Google</button>
                </form>
            ) : (
                <form onSubmit={onUserSignUpSubmit}>
                    <h2>User Sign Up</h2>
                    <input type='text' name='name' placeholder='Full Name' onChange={(e) => setUserSignUpState({ ...userSignUpState, name: e.target.value })} />
                    <input type='email' name='email' placeholder='Email' onChange={(e) => setUserSignUpState({ ...userSignUpState, email: e.target.value })} />
                    <input type='password' name='password' placeholder='Password' onChange={(e) => setUserSignUpState({ ...userSignUpState, password: e.target.value })} />
                    <button type='submit'>Sign Up</button>
                    <button type='button' onClick={handleGoogleSignIn}>Sign Up with Google</button>
                </form>
            )}
            <p>Existing user? <Link to='/login'>Log in</Link></p>
        </div>
    );
};

export default SignUp;
