import React, { useState, useContext } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { useHistory } from "react-router-dom";
import { doc, updateDoc, deleteDoc, getDoc, setDoc, arrayUnion, arrayRemove } from "firebase/firestore";
import { db } from "../firebaseInit";
import { Person, Logout, Delete, ShoppingCart } from "@mui/icons-material";
import ServiceCard2 from "../components/ServiceCard2";
import { EmailAuthProvider, reauthenticateWithCredential } from "firebase/auth";

const UserAccount = ({ userData, setUserData, onSignOutSubmit }) => {
    const history = useHistory();
    const { currentUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [userInput, setUserInput] = useState({
        name: userData?.name || "",
        address: userData?.address || "",
        phone: userData?.phone || "",
        password: "",
    });

    const handleUserInputChange = (e) => {
        setUserInput({ ...userInput, [e.target.name]: e.target.value });
    };

    const onUpdateDetailsSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const updatedUserData = {
            name: userInput.name,
            address: userInput.address,
            phone: userInput.phone,
        };

        try {
            const userRef = doc(db, "users", currentUser.uid);
            await updateDoc(userRef, updatedUserData);
            setUserData((prevData) => ({ ...prevData, ...updatedUserData }));
            alert("Profile updated successfully!");
        } catch (error) {
            console.error(`Firestore update error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const addToCart = async (service) => {
        const userRef = doc(db, "users", currentUser.uid);
        await updateDoc(userRef, {
            cart: arrayUnion(service),
        });
        setUserData((prevData) => ({ ...prevData, cart: [...(prevData?.cart || []), service] }));
    };

    const removeFromCart = async (service) => {
        const userRef = doc(db, "users", currentUser.uid);
        await updateDoc(userRef, {
            cart: arrayRemove(service),
        });
        setUserData((prevData) => ({
            ...prevData,
            cart: prevData?.cart?.filter((item) => item.serviceId !== service.serviceId) || [],
        }));
    };

    return (
        <div className="user-account-container">
            <div className="profile-section">
                <div className="profile-card">
                    <Person fontSize="large" />
                    <h3>{userData?.name}</h3>
                    <p>Email: {userData?.email}</p>
                    <p>Address: {userData?.address}</p>
                    <p>Phone: {userData?.phone}</p>
                    <button onClick={onSignOutSubmit}><Logout /> Log Out</button>
                    <button onClick={() => { }} disabled={loading}><Delete /> Delete Account</button>
                </div>
                <div className="update-form">
                    <h4>Update Profile</h4>
                    <form onSubmit={onUpdateDetailsSubmit}>
                        <input type="text" name="name" value={userInput.name} onChange={handleUserInputChange} placeholder="Full Name" />
                        <input type="text" name="address" value={userInput.address} onChange={handleUserInputChange} placeholder="Address" />
                        <input type="text" name="phone" value={userInput.phone} onChange={handleUserInputChange} placeholder="Phone" />
                        <button type="submit" disabled={loading}>Update</button>
                    </form>
                </div>
            </div>
            <div className="cart-section">
                <h4>Your Cart ({userData?.cart?.length || 0} items)</h4>
                {userData?.cart?.length > 0 ? (
                    userData.cart.map((service, index) => (
                        <ServiceCard2 key={index} passedService={service} showDelete={true} onDelete={() => removeFromCart(service)} />
                    ))
                ) : (
                    <p>Your cart is empty.</p>
                )}
            </div>
        </div>
    );
};

export default UserAccount;
