import React, { useState } from "react";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import { auth, db, RecaptchaVerifier, signInWithPhoneNumber, googleProvider, signInWithPopup } from "./firebaseConfig";
import { doc, setDoc, getDoc } from "firebase/firestore";

const Auth = () => {
    const [phone, setPhone] = useState("");
    const [name, setName] = useState("");
    const [lastName, setLastName] = useState("");
    const [otp, setOtp] = useState("");
    const [verificationId, setVerificationId] = useState(null);
    const [user, setUser] = useState(null);

    // ðŸ”¹ Setup reCAPTCHA
    const setupRecaptcha = () => {
        if (!window.recaptchaVerifier) {
            window.recaptchaVerifier = new RecaptchaVerifier(
                "recaptcha-container",
                {
                    size: "invisible",
                    callback: (response) => console.log("reCAPTCHA Verified", response),
                },
                auth
            );
        }
    };

    // ðŸ”¹ Send OTP
    const sendOtp = async () => {
        if (!phone) return alert("Please enter a phone number");

        setupRecaptcha();
        const appVerifier = window.recaptchaVerifier;

        try {
            const confirmationResult = await signInWithPhoneNumber(auth, phone, appVerifier);
            setVerificationId(confirmationResult);
            alert("OTP Sent!");
        } catch (error) {
            console.error("Error sending OTP:", error);
        }
    };

    // ðŸ”¹ Verify OTP and Save User to Firestore
    const verifyOtp = async () => {
        if (!otp || !verificationId) return alert("Enter OTP");

        try {
            const result = await verificationId.confirm(otp);
            const user = result.user;
            setUser(user);

            // Check if user already exists
            const userRef = doc(db, "users", user.uid);
            const userDoc = await getDoc(userRef);

            if (!userDoc.exists()) {
                // Assign default role (0: user)
                const userData = {
                    uid: user.uid,
                    name,
                    lastName,
                    phone: user.phoneNumber,
                    role: 0,
                };
                await setDoc(userRef, userData);
                alert("User registered successfully!");
            } else {
                alert("User already exists!");
            }
        } catch (error) {
            console.error("Error verifying OTP:", error);
        }
    };

    // ðŸ”¹ Google Sign-In
    const signInWithGoogle = async () => {
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;
            setUser(user);

            // Check if user already exists
            const userRef = doc(db, "users", user.uid);
            const userDoc = await getDoc(userRef);

            if (!userDoc.exists()) {
                const userData = {
                    uid: user.uid,
                    name: user.displayName,
                    lastName: "",
                    email: user.email,
                    role: 0,
                };
                await setDoc(userRef, userData);
                alert("Google Sign-in successful!");
            } else {
                alert("Welcome back!");
            }
        } catch (error) {
            console.error("Google Sign-in Error:", error);
        }
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h2>Sign Up</h2>

            {!user ? (
                <>
                    <div>
                        <input type="text" placeholder="First Name" value={name} onChange={(e) => setName(e.target.value)} />
                        <input type="text" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)} />
                    </div>

                    <PhoneInput country={"us"} value={phone} onChange={setPhone} />

                    <div id="recaptcha-container"></div>

                    <button onClick={sendOtp}>Send OTP</button>

                    <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
                    <button onClick={verifyOtp}>Verify OTP</button>

                    <br />
                    <button onClick={signInWithGoogle}>Sign in with Google</button>
                </>
            ) : (
                <h3>Welcome, {user.displayName || user.phoneNumber}</h3>
            )}
        </div>
    );
};

export default Auth;
