import React, { useState, useContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { signOut, deleteUser, EmailAuthProvider, reauthenticateWithCredential } from 'firebase/auth';
import { auth, db } from '../firebaseInit';
import { doc, getDoc, deleteDoc } from 'firebase/firestore';
import { Redirect } from 'react-router';
import Loading from '../components/Loading';
import UserAccount from './UserAccount';
import ProviderAccount from './ProviderAccount';

const Account = () => {
    const history = useHistory();
    const { currentUser, setCurrentUser, isProvider, setIsProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        address: '',
        phone: '',
        //userDataCart: []
    });

    const [providerData, setProviderData] = useState({
        providerDataFirstName: '',
        providerDataLastName: '',
        providerDataCompanyName: '',
        providerDataEmail: '',
        providerDataNumber: '',
        providerDataDescription: '',
        providerDataServices: []
    });

    // Run Read function on app mount:
    useEffect(() => {
        fireStoreRead();
        // eslint-disable-next-line
    }, []);

    // Read Firestore for account info:
    const fireStoreRead = async () => {
        setLoading(true);
        try {
            const userRef = doc(db, 'users', currentUser.uid);
            const userDoc = await getDoc(userRef);

            if (userDoc.exists()) {
                const data = userDoc.data();
                setUserData({
                    name: data.name,
                    email: data.email,
                    address: data.address || "",
                    phone: data.phone || "",
                    //userDataCart: data.cart || []
                });
                setIsProvider(false);
            } else {
                const providerRef = doc(db, 'providers', currentUser.uid);
                const providerDoc = await getDoc(providerRef);

                if (providerDoc.exists()) {
                    const providerData = providerDoc.data();
                    setProviderData({
                        providerDataFirstName: providerData.providerFirstName,
                        providerDataLastName: providerData.providerLastName,
                        providerDataCompanyName: providerData.providerCompanyName,
                        providerDataEmail: providerData.providerEmail,
                        providerDataNumber: providerData.providerNumber,
                        providerDataDescription: providerData.providerDescription,
                        providerDataServices: providerData.providerServices
                    });
                    setIsProvider(true);
                } else {
                    console.log(`No such users/providers found.`);
                }
            }
        } catch (error) {
            console.error("Error reading Firestore data:", error);
        } finally {
            setLoading(false);
        }
    };

    const onSignOutSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        signOut(auth).then(() => {
            setCurrentUser(null);
            setIsProvider(false);
        }).catch((error) => {
            console.log(error);
        });
        setLoading(false);
    };

    const reauthenticateUser = async (currentPassword) => {
        const credential = EmailAuthProvider.credential(currentUser.email, currentPassword);
        try {
            await reauthenticateWithCredential(currentUser, credential);
            console.log("User re-authenticated");
        } catch (error) {
            console.error("Error re-authenticating user:", error.message);
            alert("Re-authentication failed. Please try again.");
        }
    };

    const onDeleteUser = async (e) => {
        e.preventDefault();
        const confirmDelete = window.confirm("Are you sure you want to delete your account?");
        if (!confirmDelete) return;

        const currentPassword = prompt("Please enter your password to confirm:");

        if (!currentPassword) {
            alert("Password is required to delete the account.");
            return;
        }

        try {
            setLoading(true);

            // Re-authenticate the user
            await reauthenticateUser(currentPassword);

            var getWhich = 'users';
            if (isProvider) {
                getWhich = 'providers';
            }
            const user = auth.currentUser;
            await deleteDoc(doc(db, getWhich, currentUser.uid));
            await deleteUser(user);

            alert('Successfully Deleted!');
            history.push('/login');
        } catch (error) {
            console.error("Error deleting user:", error.message);
            alert("Failed to delete account. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    if (currentUser !== null) {
        return (
            <div className='ninetyfiveperc-container'>
                {loading ? <Loading /> : <>
                    {!isProvider ?
                        <UserAccount userData={userData} setUserData={setUserData} onSignOutSubmit={onSignOutSubmit} onDeleteUser={onDeleteUser} />
                        : <ProviderAccount providerData={providerData} setProviderData={setProviderData} onSignOutSubmit={onSignOutSubmit} onDeleteUser={onDeleteUser} />
                    }
                </>}
            </div>
        );
    } else {
        return <Redirect to='/' />;
    }
};

export default Account;
