import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import {
    createUserWithEmailAndPassword,
    signInWithPopup,
    sendEmailVerification
} from 'firebase/auth';
import { auth, googleProvider, db } from '../firebaseInit';
import { doc, setDoc } from 'firebase/firestore';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import { Button } from '@mui/material';
import Loading from '../components/Loading';
import './UserSignUpForm.css';

const UserSignUpForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [userSignUpState, setUserSignUpState] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        phoneNumber: ''
    });

    const handleChange = (e) => {
        setUserSignUpState({
            ...userSignUpState,
            [e.target.name]: e.target.value
        });
    };

    const handleSignUp = async (e) => {
        e.preventDefault();
        if (userSignUpState.password !== userSignUpState.confirmPassword) {
            alert('Passwords do not match!');
            return;
        }
        setLoading(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, userSignUpState.email, userSignUpState.password);
            const user = userCredential.user;

            // Send email verification
            await sendEmailVerification(user);
            alert('A verification email has been sent. Please check your inbox and verify your email before logging in.');

            // Store user data in Firestore with "verified: false"
            await setDoc(doc(db, 'users', user.uid), {
                name: userSignUpState.name,
                email: userSignUpState.email,
                phoneNumber: userSignUpState.phoneNumber,
                verified: false, // Mark as not verified initially
            });

            setUserSignUpState({
                name: '',
                email: '',
                password: '',
                confirmPassword: '',
                phoneNumber: ''
            });

        } catch (error) {
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    const handleGoogleSignUp = async () => {
        try {
            setLoading(true);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            if (!user.emailVerified) {
                // Send email verification if not already verified
                await sendEmailVerification(user);
                alert('A verification email has been sent. Please verify your email before proceeding.');
                return;
            }

            await setDoc(doc(db, 'users', user.uid), {
                name: user.displayName,
                email: user.email,
                phoneNumber: user.phoneNumber || '',
                verified: true // Google users are already verified
            }, { merge: true });

            alert('Google Sign-In Successful!');
            history.push('/');
        } catch (error) {
            alert(`Google Sign-In Failed: ${error.message}`);
        }
        setLoading(false);
    };

    return (
        <div>
            {loading ? <Loading /> : (
                <form className="user-signup-form" onSubmit={handleSignUp}>
                    <div className="form-header">
                        <h2>Create Your Account</h2>
                        <p>Join to explore services</p>
                    </div>

                    <div className="input-field">
                        <label htmlFor="fullName">Full Name</label>
                        <input
                            type="text"
                            id="fullName"
                            name="name"
                            placeholder="Enter your full name"
                            value={userSignUpState.name}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-field">
                        <label htmlFor="email">Email Address</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            placeholder="you@example.com"
                            value={userSignUpState.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-field">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Create a strong password"
                            value={userSignUpState.password}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-field">
                        <label htmlFor="confirmPassword">Confirm Password</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            name="confirmPassword"
                            placeholder="Confirm your password"
                            value={userSignUpState.confirmPassword}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <PhoneInput
                        country={'in'}
                        value={userSignUpState.phoneNumber}
                        onChange={(phone) => setUserSignUpState({ ...userSignUpState, phoneNumber: phone })}
                    />

                    <Button type='submit'>Sign Up</Button>
                    <Button onClick={handleGoogleSignUp}>Sign Up with Google</Button>
                </form>
            )}
        </div>
    );
};

export default UserSignUpForm;
