import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import {
    createUserWithEmailAndPassword,
    signInWithPopup,
    sendEmailVerification
} from 'firebase/auth';
import { auth, googleProvider, db } from '../firebaseInit';
import { doc, setDoc } from 'firebase/firestore';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import { Button } from '@mui/material';
import Loading from '../components/Loading';

const ProviderSignUpForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [providerSignUpState, setProviderSignUpState] = useState({
        name: '',
        companyName: '',
        email: '',
        phoneNumber: '',
        description: '',
        password: '',
        confirmPassword: ''
    });

    const handleChange = (e) => {
        setProviderSignUpState({
            ...providerSignUpState,
            [e.target.name]: e.target.value
        });
    };

    const handleSignUp = async (e) => {
        e.preventDefault();
        if (providerSignUpState.password !== providerSignUpState.confirmPassword) {
            alert('Passwords do not match!');
            return;
        }
        setLoading(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, providerSignUpState.email, providerSignUpState.password);
            const user = userCredential.user;

            // Send email verification
            await sendEmailVerification(user);
            alert('A verification email has been sent. Please check your inbox and verify your email before logging in.');

            // Store provider data in Firestore with "verified: false"
            await setDoc(doc(db, 'providers', user.uid), {
                name: providerSignUpState.name,
                companyName: providerSignUpState.companyName,
                email: providerSignUpState.email,
                phoneNumber: providerSignUpState.phoneNumber,
                description: providerSignUpState.description,
                role: 1,
                verified: false,
            });

            setProviderSignUpState({
                name: '',
                companyName: '',
                email: '',
                phoneNumber: '',
                description: '',
                password: '',
                confirmPassword: ''
            });
        } catch (error) {
            console.error("Error during sign-up:", error.message);
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    const handleGoogleSignUp = async () => {
        try {
            setLoading(true);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            if (!user.emailVerified) {
                await sendEmailVerification(user);
                alert('A verification email has been sent. Please verify your email before proceeding.');
                return;
            }

            await setDoc(doc(db, 'providers', user.uid), {
                name: user.displayName,
                email: user.email,
                phoneNumber: user.phoneNumber || '',
                companyName: '',
                description: '',
                role: 1,
                verified: true
            }, { merge: true });

            alert('Google Sign-In Successful!');
            history.push('/');
        } catch (error) {
            console.error("Google Sign-In Failed:", error.message);
            alert(`Google Sign-In Failed: ${error.message}`);
        }
        setLoading(false);
    };

    return (
        <div className="signup-container">
            <div className="signup-card">
                <div className="form-header">
                    <h2>Service Provider Registration</h2>
                    <p>Create an account to offer your services</p>
                </div>

                <form onSubmit={handleSignUp}>
                    <div className="form-group">
                        <label htmlFor="businessName">Business Name</label>
                        <input
                            type="text"
                            id="businessName"
                            className="form-input"
                            name="companyName"
                            placeholder="Your business name"
                            value={providerSignUpState.companyName}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Business Email</label>
                        <input
                            type="email"
                            id="email"
                            className="form-input"
                            name="email"
                            placeholder="business@example.com"
                            value={providerSignUpState.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="provider-fields">
                        <div className="form-group">
                            <label htmlFor="phone">Contact Number</label>
                            <PhoneInput
                                country={'in'}
                                value={providerSignUpState.phoneNumber}
                                onChange={(phone) => setProviderSignUpState({ ...providerSignUpState, phoneNumber: phone })}
                                inputProps={{
                                    id: 'phone',
                                    className: 'form-input',
                                    required: true
                                }}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address">Business Address</label>
                            <input
                                type="text"
                                id="address"
                                className="form-input"
                                name="description"
                                placeholder="Your business address"
                                value={providerSignUpState.description}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            className="form-input"
                            name="password"
                            placeholder="Create a secure password"
                            value={providerSignUpState.password}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="confirmPassword">Confirm Password</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            className="form-input"
                            name="confirmPassword"
                            placeholder="Confirm your password"
                            value={providerSignUpState.confirmPassword}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <button type="submit" className="submit-btn">
                        Register as Provider
                    </button>

                    <p className="login-link">
                        Already registered? <Link to="/login">Log in</Link>
                    </p>
                </form>
            </div>
        </div>
    );
};

export default ProviderSignUpForm;
