import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { doc, updateDoc, getDoc } from 'firebase/firestore';
import { db } from '../firebaseInit';
import ServiceCard2 from '../components/ServiceCard2';
import {
    FormControl,
    Select,
    MenuItem,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Button as MuiButton,
    Grid,
    CircularProgress
} from '@mui/material';
import { Person, Logout, Delete } from '@mui/icons-material';
import { globalIconStyle } from '../assets/GlobalStyles';
import './ProviderAccount.css';

const ProviderAccount = ({ onSignOutSubmit, onDeleteUser }) => {
    const { currentUser } = useContext(AuthContext);
    const [providerData, setProviderData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [serviceInput, setServiceInput] = useState({
        serviceId: null,
        serviceTitle: '',
        serviceDescription: '',
        serviceCategory: '',
        servicePrice: '',
    });

    useEffect(() => {
        const fetchProviderData = async () => {
            if (!currentUser) return;
            try {
                const providerRef = doc(db, 'providers', currentUser.uid);
                const providerSnap = await getDoc(providerRef);
                if (providerSnap.exists()) {
                    setProviderData(providerSnap.data());
                } else {
                    setError('No provider data found.');
                }
            } catch (err) {
                console.error("Error fetching provider data:", err.message);
                setError('Failed to load provider details.');
            } finally {
                setLoading(false);
            }
        };
        fetchProviderData();
    }, [currentUser]);

    // Handle input change
    const handleServiceChange = (e) => {
        const { name, value } = e.target;
        setServiceInput(prev => ({ ...prev, [name]: value }));
    };

    // Edit service
    const onEditService = (service) => {
        setServiceInput(service);
    };

    // Delete service
    const onDeleteService = async (serviceId) => {
        const confirmDelete = prompt("Type 'yes' to confirm service deletion:");
        if (confirmDelete?.toLowerCase() !== 'yes') {
            alert("Deletion cancelled.");
            return;
        }

        try {
            const updatedServices = (providerData?.services || []).filter(s => s.serviceId !== serviceId);
            await updateDoc(doc(db, 'providers', currentUser.uid), { services: updatedServices });
            setProviderData(prev => ({ ...prev, services: updatedServices }));
        } catch (err) {
            console.error("Service deletion failed:", err.message);
            alert("Error deleting service: " + err.message);
        }
    };

    // Add or Update Service
    const onAddServiceSubmit = async (e) => {
        e.preventDefault();
        if (!serviceInput.serviceTitle || !serviceInput.serviceCategory || !serviceInput.servicePrice) {
            alert("Please fill in all required fields.");
            return;
        }

        setLoading(true);
        const services = providerData?.services || [];
        let updatedServices;

        if (serviceInput.serviceId) {
            // Update existing service
            updatedServices = services.map(s => s.serviceId === serviceInput.serviceId ? serviceInput : s);
        } else {
            // Add new service
            const newService = { ...serviceInput, serviceId: Date.now() };
            updatedServices = [...services, newService];
        }

        try {
            await updateDoc(doc(db, 'providers', currentUser.uid), { services: updatedServices });
            setProviderData(prev => ({ ...prev, services: updatedServices }));
            setServiceInput({ serviceId: null, serviceTitle: '', serviceDescription: '', serviceCategory: '', servicePrice: '' });
        } catch (err) {
            console.error("Service update failed:", err.message);
            alert("Error saving service: " + err.message);
        } finally {
            setLoading(false);
        }
    };

    // Update Profile
    const [openUpdate, setOpenUpdate] = useState(false);
    const [updateData, setUpdateData] = useState(null);

    useEffect(() => {
        if (providerData) setUpdateData(providerData);
    }, [providerData]);

    const handleUpdateChange = (e) => {
        const { name, value } = e.target;
        setUpdateData(prev => ({ ...prev, [name]: value }));
    };

    const handleUpdateSubmit = async () => {
        try {
            await updateDoc(doc(db, 'providers', currentUser.uid), updateData);
            setProviderData(updateData);
            setOpenUpdate(false);
        } catch (err) {
            alert("Profile update failed: " + err.message);
        }
    };

    if (loading) return <CircularProgress />;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <>
            {/* Profile Card */}
            <div className='card-type1 account-card'>
                <div className='flex-column-stretch'>
                    <div className='flex-row center'>
                        <div className='circle size100px'><Person style={globalIconStyle} /></div>
                    </div>
                    <h3 className='heading-type3 center-text'>{providerData?.companyName}</h3>
                    <p><strong>Account Manager:</strong> {providerData?.firstName} {providerData?.lastName}</p>
                    <p><strong>Email:</strong> {providerData?.email}</p>
                    <p><strong>Phone:</strong> {providerData?.phone}</p>
                    <p><strong>About Your Company:</strong> {providerData?.description}</p>
                    <MuiButton onClick={onSignOutSubmit} startIcon={<Logout />}>Log Out</MuiButton>
                    <MuiButton onClick={onDeleteUser} color="error" startIcon={<Delete />}>Delete Account</MuiButton>
                    <MuiButton onClick={() => setOpenUpdate(true)}>Update Details</MuiButton>
                </div>
            </div>

            {/* Services List */}
            <div className='card-type1 account-card'>
                <h4>Your Services: {providerData?.services?.length || 0}</h4>
                <div className="dark-grey-container">
                    {providerData?.services?.length > 0 ? (
                        providerData.services.map((service, index) => (
                            <ServiceCard2
                                key={service.serviceId || index}
                                passedService={service}
                                passedIndex={index}
                                showDelete={true}
                                onDeleteItem={onDeleteService}
                                onEditService={onEditService}
                            />
                        ))
                    ) : (
                        <p>No Services Added</p>
                    )}
                </div>
            </div>

            {/* Add/Edit Service Form */}
            <div className='card-type1 account-card'>
                <form onSubmit={onAddServiceSubmit}>
                    <h4>{serviceInput.serviceId ? "Edit Service" : "Add a New Service"}</h4>
                    <TextField label='Service Title' name='serviceTitle' fullWidth value={serviceInput.serviceTitle} onChange={handleServiceChange} />
                    <TextField label='Service Price (Rs.)' name='servicePrice' fullWidth type='number' value={serviceInput.servicePrice} onChange={handleServiceChange} />
                    <MuiButton type='submit' variant='contained'>{serviceInput.serviceId ? "Update Service" : "Submit"}</MuiButton>
                </form>
            </div>
        </>
    );
};

export default ProviderAccount;
