import React, { useState } from 'react';
import { TextField, Button, Grid } from '@mui/material';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { auth, db } from '../firebaseInit';
import { useHistory } from 'react-router-dom';

const ProviderSignupForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        companyName: '',
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        description: '',
        password: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
            await setDoc(doc(db, 'providers', userCredential.user.uid), {
                companyName: formData.companyName,
                firstName: formData.firstName,
                lastName: formData.lastName,
                email: formData.email,
                phone: formData.phone,
                description: formData.description,
                services: [],
                role: 1,
                uid: userCredential.user.uid,
            });
            history.push('/');
        } catch (error) {
            alert(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSignup}>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <TextField fullWidth label="Company Name" name="companyName" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="First Name" name="firstName" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Last Name" name="lastName" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Email" name="email" type="email" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Phone" name="phone" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Description" name="description" multiline rows={3} onChange={handleChange} />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Password" name="password" type="password" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <Button fullWidth variant="contained" type="submit" disabled={loading}>
                        Sign Up as Provider
                    </Button>
                </Grid>
            </Grid>
        </form>
    );
};

export default ProviderSignupForm;
