import React, { useContext, useState, useEffect } from 'react'
import { AuthContext } from '../contexts/AuthContext'
import { doc, collection, query, getDoc, getDocs, updateDoc } from 'firebase/firestore'
import { db } from '../firebaseInit'
import Categories from '../components/Categories'
import ServiceCard from '../components/ServiceCard'
import Loading from '../components/Loading'

const Services = () => {

    const { currentUser, isProvider } = useContext(AuthContext)
    const [loading, setLoading] = useState(false)
    const [cleaningServices, setCleaningServices] = useState([])
    const [electricianServices, setElectricianServices] = useState([])
    const [plumberServices, setPlumberServices] = useState([])
    const [carpenterServices, setCarpenterServices] = useState([])
    const [pestControlServices, setPestControlServices] = useState([])
    const [womenSalonServices, setWomenSalonServices] = useState([])
    const [menSalonServices, setMenSalonServices] = useState([])
    // Removed miscellaniousServices state

    const [userData, setUserData] = useState({
        userDataFirstName: '',
        userDataLastName: '',
        userDataEmail: '',
        userDataCart: []
    })

    useEffect(() => {
        readServices().then(readUserData())
        // eslint-disable-next-line
    }, [])

    const readServices = async () => {
        setLoading(true)
        const q = query(collection(db, 'providers'))
        const docSnap = await getDocs(q)
        let readData = []
        if (docSnap) {
            docSnap.forEach((doc) => {
                // use fallback empty array if providerServices is undefined
                readData.push(doc.data() || {})
            })
            // Combine providerServices into one array; ensure each doc's providerServices exists
            let allServices = [].concat(...readData.map(o => o.providerServices || []))
            // Filter out undefined values
            allServices = allServices.filter(service => service !== undefined && service !== null)
            setCleaningServices(allServices.filter(eachService => eachService.serviceCategory === 'Cleaning'))
            setElectricianServices(allServices.filter(eachService => eachService.serviceCategory === 'Electricians'))
            setPlumberServices(allServices.filter(eachService => eachService.serviceCategory === 'Plumbers'))
            setCarpenterServices(allServices.filter(eachService => eachService.serviceCategory === 'Carpenters'))
            setPestControlServices(allServices.filter(eachService => eachService.serviceCategory === 'Pest Control'))
            setWomenSalonServices(allServices.filter(eachService => eachService.serviceCategory === 'Salon for Women'))
            setMenSalonServices(allServices.filter(eachService => eachService.serviceCategory === 'Salon for Men'))
        } else {
            console.log('not found')
        }
        setLoading(false)
    }

    const readUserData = async () => {
        if (currentUser != null) {
            var docSnap = await getDoc(doc(db, 'users', currentUser.uid))
            if (docSnap.exists()) {
                console.log(`Fetched user data:`, docSnap.data())
                setUserData({
                    ...userData,
                    userDataFirstName: docSnap.data().userFirstName,
                    userDataLastName: docSnap.data().userLastName,
                    userDataEmail: docSnap.data().userEmail,
                    userDataCart: docSnap.data().userCart
                })
            } else {
                console.log(`Not signed in by user.`)
            }
        }
    }

    const onAddToCart = async (passedCategory, passedIndex) => {
        setLoading(true)
        if (currentUser != null) {
            var whichServices = null
            console.log(`Adding ${passedCategory} ${passedIndex} to cart.`)
            // <MenuItem value={'Plumbers'}>Plumbers</MenuItem>
            // <MenuItem value={'Carpenters'}>Carpenters</MenuItem>
            // <MenuItem value={'Pest Control'}>Pest Control</MenuItem>
            // <MenuItem value={'Salon for Women'}>Salon for Women</MenuItem>
            // <MenuItem value={'Salon for Men'}>Salon for Men</MenuItem>
            // <MenuItem value={'Massage'}>Massage</MenuItem>
            // <MenuItem value={'Miscellanious'}>Miscellanious</MenuItem>
            if (passedCategory === 'Cleaning') {
                whichServices = cleaningServices
            } else if (passedCategory === 'Electricians') {
                whichServices = electricianServices
            } else if (passedCategory === 'Plumbers') {
                whichServices = plumberServices
            } else if (passedCategory === 'Carpenters') {
                whichServices = carpenterServices
            } else if (passedCategory === 'Pest Control') {
                whichServices = pestControlServices
            } else if (passedCategory === 'Salon for Women') {
                whichServices = womenSalonServices
            } else if (passedCategory === 'Salon for Men') {
                whichServices = menSalonServices
            } else {
                console.log(`passedCategory: ${passedCategory} doesnt match.`)
            }
            const addedService = whichServices[passedIndex]
            const finalCart = [...userData.userDataCart, addedService]
            setUserData(userData => ({
                ...userData,
                userDataCart: finalCart
            }))
            await updateDoc(doc(db, 'users', currentUser.uid), {
                userCart: finalCart
            }).catch((error) => {
                console.log(`in services/addToCart/fireStore_upDoc: Error Code ${error.code}: ${error.message}`)
                setLoading(false)
                return
            })
            alert(`Added ${addedService.serviceTitle} by ${addedService.serviceProvider} to cart.`)
        } else {
            alert(`Please Login or Signup first.`)
        }
        setLoading(false)
    }

    // Ensure services is an array and filter out undefined values
    const validCleaningServices = (cleaningServices || []).filter(service => service);
    const validElectricianServices = (electricianServices || []).filter(service => service);
    const validPlumberServices = (plumberServices || []).filter(service => service);
    const validCarpenterServices = (carpenterServices || []).filter(service => service);
    const validPestControlServices = (pestControlServices || []).filter(service => service);
    const validWomenSalonServices = (womenSalonServices || []).filter(service => service);
    const validMenSalonServices = (menSalonServices || []).filter(service => service);
    // Removed validMiscellaniousServices

    return (
        <>
            <div className='eightyperc-container'>
                <div className='card-type1'>
                    <div className='ninetyfiveperc-container'>
                        <h2 className='heading-type3'>Services</h2>
                        <p>Choose from the best services at the lowest prices:</p>
                    </div>
                </div>
                <Categories />
            </div>
            <div className='eightyperc-container'>
                <div className='card-type1' id='Cleaning'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Cleaning:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validCleaningServices.length !== 0 ? (
                                        validCleaningServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`cleaning-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Cleaning Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Electricians'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1'>Electricians:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validElectricianServices.length !== 0 ? (
                                        validElectricianServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`electrician-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Electrician Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Plumbers'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Plumbers:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validPlumberServices.length !== 0 ? (
                                        validPlumberServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`plumber-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Plumber Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Carpenters'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Carpenters:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validCarpenterServices.length !== 0 ? (
                                        validCarpenterServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`carpenter-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Plumber Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Pest Control'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Pest Control:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validPestControlServices.length !== 0 ? (
                                        validPestControlServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`pestcontrol-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Pest Control Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Salon for Women'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Salon for Women:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validWomenSalonServices.length !== 0 ? (
                                        validWomenSalonServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`womensalon-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Women's Salon Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className='card-type1' id='Salon for Men'>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1' >Salon for Men:</h3>
                            <div className="flex-column-stretch dark-grey-container">
                                {loading ? <Loading /> : (
                                    validMenSalonServices.length !== 0 ? (
                                        validMenSalonServices.map((eachService, index) => (
                                            <ServiceCard
                                                key={`mensalon-${eachService.serviceId || index}`}
                                                passedService={eachService}
                                                passedIndex={index}
                                                showButton={!isProvider}
                                                onAddToCart={onAddToCart}
                                            />
                                        ))
                                    ) : (
                                        <div className='flex-row'>
                                            <p className='para-type2'>No Men's Salon Services</p>
                                        </div>
                                    )
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                {/* Removed the Miscellanious category block */}
            </div>
        </>
    )
}

export default Services
