import React, { useState, useContext, useEffect } from "react";
import "./UserAccount.css";  // Add this line
import { AuthContext } from "../contexts/AuthContext";
import { useHistory } from "react-router-dom";
import { doc, updateDoc, deleteDoc, getDoc, setDoc, arrayUnion, arrayRemove } from "@firebase/firestore";
import { db } from "../firebaseInit";
import { Person, Logout, Delete, ShoppingCart } from "@mui/icons-material";
import { globalIconStyle } from "../assets/GlobalStyles";
import ServiceCard2 from "../components/ServiceCard2";
import { EmailAuthProvider, reauthenticateWithCredential } from "firebase/auth";

const UserAccount = ({ userData, setUserData, onSignOutSubmit }) => {
    const history = useHistory();
    const { currentUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [cart, setCart] = useState([]);

    const [userInput, setUserInput] = useState({
        name: userData?.name || "",
        address: userData?.address || "",
        phone: userData?.phone || "",
        password: "",
    });

    // Fetch user cart data
    useEffect(() => {
        const fetchCart = async () => {
            if (currentUser) {
                const userRef = doc(db, "users", currentUser.uid);
                const userDoc = await getDoc(userRef);
                if (userDoc.exists()) {
                    setCart(userDoc.data().cart || []);
                }
            }
        };
        fetchCart();
    }, [currentUser]);

    const handleUserInputChange = (e) => {
        setUserInput({
            ...userInput,
            [e.target.name]: e.target.value,
        });
    };

    // Update user details
    const onUpdateDetailsSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const updatedUserData = {
            name: userInput.name,
            address: userInput.address,
            phone: userInput.phone,
        };

        try {
            const userRef = doc(db, "users", currentUser.uid);
            await updateDoc(userRef, updatedUserData);
            setUserData((prevData) => ({
                ...prevData,
                ...updatedUserData,
            }));
            alert("Profile updated successfully!");
        } catch (error) {
            console.error(`Firestore update error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    // Add item to cart
    const addToCart = async (service) => {
        if (!currentUser) return alert("Please log in first!");

        const userRef = doc(db, "users", currentUser.uid);
        try {
            await updateDoc(userRef, {
                cart: arrayUnion(service),
            });
            setCart([...cart, service]);
            alert("Added to cart!");
        } catch (error) {
            console.error("Error adding to cart:", error);
            alert("Failed to add to cart. Please try again.");
        }
    };

    // Remove item from cart
    const removeFromCart = async (service) => {
        if (!currentUser) return alert("Please log in first!");

        const userRef = doc(db, "users", currentUser.uid);
        try {
            await updateDoc(userRef, {
                cart: arrayRemove(service),
            });
            setCart(cart.filter((item) => item.id !== service.id));
            alert("Removed from cart!");
        } catch (error) {
            console.error("Error removing from cart:", error);
            alert("Failed to remove from cart. Please try again.");
        }
    };

    // Delete user account
    const onDeleteUser = async () => {
        const confirmDelete = window.confirm("Are you sure you want to delete your account?");
        if (!confirmDelete) return;

        const currentPassword = prompt("Please enter your password to confirm:");

        if (!currentPassword) {
            alert("Password is required to delete the account.");
            return;
        }

        try {
            setLoading(true);
            await reauthenticateWithCredential(currentUser, EmailAuthProvider.credential(currentUser.email, currentPassword));
            await deleteDoc(doc(db, "users", currentUser.uid));
            await currentUser.delete();
            alert("Account deleted successfully!");
            history.push("/signup");
        } catch (error) {
            console.error("Error deleting user:", error.message);
            alert("Failed to delete account. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <div className="eightyperc-container">
                <div className="flex-row">
                    <div className="card-type1 account-card">
                        <div className="flex-column-stretch eightyperc-container">
                            <div className="flex-row">
                                <div className="circle size100px">
                                    <Person style={globalIconStyle} />
                                </div>
                            </div>
                            <h3 className="heading-type3 center-text">{userData?.name}</h3>
                            <div className="flex-row">
                                <p className="para-type2">Email:</p>
                                <p className="grey-container">{userData?.email}</p>
                            </div>
                            <div className="flex-row left-justify">
                                <p className="para-type2">Address:</p>
                                <p className="grey-container">{userData?.address}</p>
                            </div>
                            <div className="flex-row">
                                <p className="para-type2">Phone Number:</p>
                                <p className="grey-container">{userData?.phone}</p>
                            </div>
                            <div className="flex-row">
                                <button className="button-type1" onClick={onSignOutSubmit}>
                                    <Logout />
                                    Log Out
                                </button>
                                <button className="button-type2" onClick={onDeleteUser} disabled={loading}>
                                    <Delete />
                                    Delete Account
                                </button>
                            </div>
                        </div>
                    </div>
                    <div className="card-type1 account-card auto-side-margin">
                        <form onSubmit={onUpdateDetailsSubmit} className="eightyperc-container">
                            <h4 className="heading-type3">Update Profile</h4>
                            <div className="flex-column-stretch">
                                <p>Full Name: </p>
                                <input type="text" name="name" value={userInput.name} onChange={handleUserInputChange} />
                                <p>Address: </p>
                                <textarea name="address" value={userInput.address} onChange={handleUserInputChange} />
                                <p>Phone Number: </p>
                                <input type="text" name="phone" value={userInput.phone} onChange={handleUserInputChange} />
                                <input type="submit" className="button" value="Update" disabled={loading} />
                            </div>
                        </form>
                    </div>
                </div>
            </div>

            <div className="flex-column">
                <div className="card-type1 eightyperc-container">
                    <h4 className="heading-type3">Your Cart Items: {cart.length}</h4>
                    <div className="flex-column-stretch dark-grey-container">
                        {cart.length ? (
                            cart.map((service, index) => (
                                <ServiceCard2 key={index} passedService={service} showDelete={true} onDelete={() => removeFromCart(service)} />
                            ))
                        ) : (
                            <p className="para-type2">Cart is Empty</p>
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export default UserAccount;
