import React from 'react';
import { Dialog, DialogContent, DialogTitle, IconButton } from '@mui/material';
import { Close } from '@mui/icons-material';
import './ServiceImageModal.css';

const ServiceImageModal = ({ open, onClose, service, relatedServices }) => {
    return (
        <Dialog
            open={open}
            onClose={onClose}
            maxWidth="md"
            fullWidth
        >
            <DialogTitle>
                <div className="modal-header">
                    <h3>{service?.serviceTitle}</h3>
                    <IconButton onClick={onClose}>
                        <Close />
                    </IconButton>
                </div>
            </DialogTitle>
            <DialogContent>
                <div className="service-image-large">
                    <img src={service?.imageURL} alt={service?.serviceTitle} />
                </div>

                <div className="related-services">
                    <h4>Related Services in {service?.serviceCategory}</h4>
                    <div className="related-services-grid">
                        {relatedServices?.map((relatedService, index) => (
                            <div key={index} className="related-service-card">
                                <img src={relatedService.imageURL} alt={relatedService.serviceTitle} />
                                <div className="service-info">
                                    <h5>{relatedService.serviceTitle}</h5>
                                    <p>Rs. {relatedService.servicePrice}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </DialogContent>
        </Dialog>
    );
};

export default ServiceImageModal;
