import React, { useState, useContext } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { useHistory } from "react-router-dom";
import { doc, updateDoc, deleteDoc } from "@firebase/firestore";
import { db } from "../firebaseInit";
import { Person, Logout, Delete, ShoppingCart } from "@mui/icons-material";
import { globalIconStyle } from "../assets/GlobalStyles";
import ServiceCard2 from "../components/ServiceCard2";
import { EmailAuthProvider, reauthenticateWithCredential } from "firebase/auth";

const UserAccount = ({ userData, setUserData, onSignOutSubmit }) => {
    const history = useHistory();
    const { currentUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [userInput, setUserInput] = useState({
        name: userData?.name || "",
        address: userData?.address || "",
        phone: userData?.phone || "",
        password: "",
    });

    const handleUserInputChange = (e) => {
        setUserInput({
            ...userInput,
            [e.target.name]: e.target.value,
        });
    };

    const onUpdateDetailsSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const updatedUserData = {
            name: userInput.name,
            address: userInput.address,
            phone: userInput.phone,
        };

        try {
            await updateDoc(doc(db, "users", currentUser.uid), updatedUserData);
            setUserData((prevData) => ({
                ...prevData,
                ...updatedUserData,
            }));
            alert("Profile updated successfully!");
        } catch (error) {
            console.error(`Firestore update error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const reauthenticateUser = async (currentPassword) => {
        const credential = EmailAuthProvider.credential(currentUser.email, currentPassword);
        try {
            await reauthenticateWithCredential(currentUser, credential);
            console.log("User re-authenticated");
        } catch (error) {
            console.error("Error re-authenticating user:", error.message);
            alert("Re-authentication failed. Please try again.");
        }
    };

    const onDeleteUser = async () => {
        const confirmDelete = window.confirm("Are you sure you want to delete your account?");
        if (!confirmDelete) return;

        const currentPassword = prompt("Please enter your password to confirm:");

        if (!currentPassword) {
            alert("Password is required to delete the account.");
            return;
        }

        try {
            setLoading(true);

            // Re-authenticate the user
            await reauthenticateUser(currentPassword);

            // Delete user document from Firestore
            await deleteDoc(doc(db, "users", currentUser.uid));

            // Delete the authentication user
            await currentUser.delete();

            alert("Account deleted successfully!");

            // Redirect to Signup page
            history.push("/signup");
        } catch (error) {
            console.error("Error deleting user:", error.message);
            alert("Failed to delete account. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <div className="eightyperc-container">
                <div className="flex-row">
                    <div className="card-type1 account-card">
                        <div className="flex-column-stretch eightyperc-container">
                            <div className="flex-row">
                                <div className="circle size100px">
                                    <Person style={globalIconStyle} />
                                </div>
                            </div>
                            <h3 className="heading-type3 center-text">{userData?.name}</h3>
                            <div className="flex-row">
                                <p className="para-type2">Email:</p>
                                <p className="grey-container">{userData?.email}</p>
                            </div>
                            <div className="flex-row left-justify">
                                <p className="para-type2">Address:</p>
                                <p className="grey-container">{userData?.address}</p>
                            </div>
                            <div className="flex-row">
                                <p className="para-type2">Phone Number:</p>
                                <p className="grey-container">{userData?.phone}</p>
                            </div>
                            <div className="flex-row">
                                <button className="button-type1" onClick={onSignOutSubmit}>
                                    <Logout />
                                    Log Out
                                </button>
                                <button className="button-type2" onClick={onDeleteUser} disabled={loading}>
                                    <Delete />
                                    Delete Account
                                </button>
                            </div>
                        </div>
                    </div>
                    <div className="card-type1 account-card auto-side-margin">
                        <form onSubmit={onUpdateDetailsSubmit} className="eightyperc-container">
                            <h4 className="heading-type3">Update Profile</h4>
                            <div className="flex-column-stretch">
                                <div className="flex-column-stretch signup-description">
                                    <p>Full Name: </p>
                                    <input
                                        type="text"
                                        name="name"
                                        placeholder="Enter full name"
                                        value={userInput.name}
                                        onChange={handleUserInputChange}
                                    />
                                </div>
                                <div className="flex-column-stretch signup-description">
                                    <p>Address: </p>
                                    <textarea
                                        name="address"
                                        placeholder="Enter your address..."
                                        value={userInput.address}
                                        onChange={handleUserInputChange}
                                    />
                                </div>
                                <div className="flex-row">
                                    <p>Phone Number: </p>
                                    <input
                                        type="text"
                                        name="phone"
                                        placeholder="+91..."
                                        value={userInput.phone}
                                        onChange={handleUserInputChange}
                                    />
                                </div>
                                <div className="flex-row">
                                    <p>Password: </p>
                                    <input
                                        type="password"
                                        name="password"
                                        placeholder="Enter new password"
                                        value={userInput.password}
                                        onChange={handleUserInputChange}
                                    />
                                </div>
                                <div className="flex-row">
                                    <input type="submit" className="button" value="Update" disabled={loading} />
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
            <div className="flex-column">
                <div className="card-type1 eightyperc-container">
                    <div className="flex-column-stretch ninetyfiveperc-container">
                        <h4 className="heading-type3">Your Cart Items: {userData?.userCart?.length || 0}</h4>
                        <div className="flex-column-stretch dark-grey-container">
                            {userData?.userCart?.length ? (
                                userData.userCart.map((eachService, serviceIndex) => (
                                    <ServiceCard2 key={serviceIndex} passedService={eachService} showDelete={true} />
                                ))
                            ) : (
                                <div className="flex-row">
                                    <p className="para-type2">Cart is Empty</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default UserAccount;
