import React, { useState, useContext } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { doc, updateDoc } from "firebase/firestore";
import { db, storage } from "../firebaseInit";
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import ServiceCard2 from "../components/ServiceCard2";
import { Checkbox, FormControl, Select, MenuItem } from "@mui/material";
import { Person, Logout, Delete } from "@mui/icons-material";
import { globalIconStyle } from "../assets/GlobalStyles";

const ProviderAccount = ({ providerData, setProviderData, onSignOutSubmit, onDeleteUser }) => {
    const { currentUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [serviceInput, setServiceInput] = useState({
        serviceId: 0,
        serviceTitle: "",
        serviceDescription: "",
        serviceCategory: "",
        servicePrice: "",
        serviceAvailable: true,
        serviceImageDownloadURL: "",
    });

    const handleServiceInputChange = (e) => {
        setServiceInput({ ...serviceInput, [e.target.name]: e.target.value });
    };

    const handleServiceImageInputChange = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        setLoading(true);
        const storageRef = ref(storage, `serviceImages/${providerData.providerDataCompanyName}/${file.name}`);

        try {
            await uploadBytes(storageRef, file);
            const downloadURL = await getDownloadURL(storageRef);
            setServiceInput((prev) => ({ ...prev, serviceImageDownloadURL: downloadURL }));
        } catch (error) {
            console.error("Error uploading image:", error);
        }
        setLoading(false);
    };

    const onAddServiceSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const newServiceId = providerData.providerDataServices.length + 1;
        const newService = {
            ...serviceInput,
            serviceId: newServiceId,
            serviceProvider: providerData.providerDataCompanyName,
        };

        const finalServices = [...providerData.providerDataServices, newService];
        setProviderData({ ...providerData, providerDataServices: finalServices });

        try {
            await updateDoc(doc(db, "providers", currentUser.uid), { providerServices: finalServices });
        } catch (error) {
            console.error("Firestore update failed:", error);
        }

        setLoading(false);
        setServiceInput({
            serviceId: 0,
            serviceTitle: "",
            serviceDescription: "",
            serviceCategory: "",
            servicePrice: "",
            serviceAvailable: true,
            serviceImageDownloadURL: "",
        });
    };

    return (
        <>
            <div className="flex-row">
                <div className="card-type1 account-card">
                    <div className="flex-column-stretch eightyperc-container">
                        <div className="flex-row">
                            <div className="circle size100px">
                                <Person style={globalIconStyle} />
                            </div>
                        </div>
                        <h3 className="heading-type3 center-text">{providerData.providerDataCompanyName}</h3>
                        <p>Email: {providerData.providerDataEmail}</p>
                        <p>Phone: {providerData.providerDataNumber}</p>
                        <p>Description: {providerData.providerDataDescription}</p>
                        <button className="button-type1" onClick={onSignOutSubmit}><Logout /> Log Out</button>
                        <button className="button-type2" onClick={onDeleteUser}><Delete /> Delete Account</button>
                    </div>
                </div>
                <div className="card-type1 account-card">
                    <h4>Your Services: {providerData.providerDataServices.length}</h4>
                    <div className="dark-grey-container">
                        {providerData.providerDataServices.length ? providerData.providerDataServices.map((service, index) => (
                            <ServiceCard2 key={index} passedService={service} showDelete={false} />
                        )) : <p>No Services Added</p>}
                    </div>
                </div>
            </div>
            <div className="card-type1 account-card">
                <form onSubmit={onAddServiceSubmit}>
                    <h4>Add a New Service</h4>
                    <input type="text" name="serviceTitle" placeholder="Service Title" value={serviceInput.serviceTitle} onChange={handleServiceInputChange} />
                    <textarea name="serviceDescription" placeholder="Service Description" value={serviceInput.serviceDescription} onChange={handleServiceInputChange} />
                    <input type="text" name="servicePrice" placeholder="Service Price" value={serviceInput.servicePrice} onChange={handleServiceInputChange} />
                    <FormControl>
                        <Select name="serviceCategory" value={serviceInput.serviceCategory} onChange={handleServiceInputChange}>
                            <MenuItem value="">Select Category</MenuItem>
                            <MenuItem value="Cleaning">Cleaning</MenuItem>
                            <MenuItem value="Electricians">Electricians</MenuItem>
                            <MenuItem value="Plumbers">Plumbers</MenuItem>
                            <MenuItem value="Carpenters">Carpenters</MenuItem>
                            <MenuItem value="Pest Control">Pest Control</MenuItem>
                            <MenuItem value="Salon for Women">Salon for Women</MenuItem>
                            <MenuItem value="Salon for Men">Salon for Men</MenuItem>
                            <MenuItem value="Massage">Massage</MenuItem>
                            <MenuItem value="Miscellaneous">Miscellaneous</MenuItem>
                        </Select>
                    </FormControl>
                    <input type="file" onChange={handleServiceImageInputChange} />
                    <Checkbox name="serviceAvailable" checked={serviceInput.serviceAvailable} onChange={(e) => setServiceInput({ ...serviceInput, serviceAvailable: e.target.checked })} /> Available Now
                    <button type="submit" disabled={loading}>Submit</button>
                </form>
            </div>
        </>
    );
};

export default ProviderAccount;
