import React, { useState } from 'react';
import { TextField, Button, Grid } from '@mui/material';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { auth, db } from '../firebaseInit';
import { useHistory } from 'react-router-dom';

const UserSignupForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        fullName: '',
        phone: '',
        address: '',
        email: '',
        password: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
            await setDoc(doc(db, 'users', userCredential.user.uid), {
                fullName: formData.fullName,
                phone: formData.phone,
                address: formData.address,
                email: formData.email,
                cart: [],
                role: 0,
                uid: userCredential.user.uid,
            });
            history.push('/');
        } catch (error) {
            alert(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <form onSubmit={handleSignup}>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <TextField fullWidth label="Full Name" name="fullName" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Phone" name="phone" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Address" name="address" onChange={handleChange} />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Email" name="email" type="email" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <TextField fullWidth label="Password" name="password" type="password" onChange={handleChange} required />
                </Grid>
                <Grid item xs={12}>
                    <Button fullWidth variant="contained" type="submit" disabled={loading}>
                        Sign Up
                    </Button>
                </Grid>
            </Grid>
        </form>
    );
};

export default UserSignupForm;
