import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { doc, updateDoc } from '@firebase/firestore';
import { db } from '../firebaseInit';
import ServiceCard2 from '../components/ServiceCard2';
import { FormControl, Select, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Button as MuiButton } from '@mui/material';
import { Person, Logout, Delete } from '@mui/icons-material';
import { globalIconStyle } from '../assets/GlobalStyles';
import './ProviderAccount.css';

const ProviderAccount = ({ providerData, setProviderData, onSignOutSubmit, onDeleteUser }) => {
    const { currentUser } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [serviceInput, setServiceInput] = useState({
        serviceId: 0,
        serviceTitle: '',
        serviceDescription: '',
        serviceCategory: '',
        servicePrice: 0.0,
        serviceProvider: '',
    });

    // New: Function to set the form for editing an existing service
    const onEditService = (service) => {
        setServiceInput(service);
    };

    // New: Delete service with confirmation
    const onDeleteService = async (serviceId) => {
        if (window.confirm("Are you sure you want to delete this service?")) {
            const updatedServices = (providerData.providerDataServices || [])
                .filter(s => String(s.serviceId) !== String(serviceId));
            try {
                await updateDoc(doc(db, 'providers', currentUser.uid), {
                    providerServices: updatedServices,
                });
                setProviderData(prev => ({ ...prev, providerDataServices: updatedServices }));
            } catch (error) {
                console.error("Service deletion failed:", error.message);
                alert("Service deletion failed: " + error.message);
            }
        }
    };

    const onAddServiceSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        // Use a fallback empty array if providerData.providerDataServices is undefined
        const currentServices = providerData.providerDataServices || [];
        let finalServices;
        // If the serviceInput already has an id, then update; otherwise add new.
        if (serviceInput.serviceId) {
            finalServices = currentServices.map(s => s.serviceId === serviceInput.serviceId ? serviceInput : s);
        } else {
            const newServiceId = Date.now();
            const newService = {
                ...serviceInput,
                serviceId: newServiceId,
                serviceProvider: providerData.providerDataCompanyName,
            };
            finalServices = [...currentServices, newService];
        }
        try {
            await updateDoc(doc(db, 'providers', currentUser.uid), {
                providerServices: finalServices,
            });
            setProviderData(prev => ({ ...prev, providerDataServices: finalServices }));
            // Reset form after update/add
            setServiceInput({
                serviceId: 0,
                serviceTitle: '',
                serviceDescription: '',
                serviceCategory: '',
                servicePrice: 0.0,
                serviceProvider: '',
            });
        } catch (error) {
            console.error(`Firestore Update Error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const [openUpdate, setOpenUpdate] = useState(false);
    const [updateData, setUpdateData] = useState({ ...providerData });

    const handleUpdateChange = (e) => {
        const { name, value } = e.target;
        setUpdateData(prev => ({ ...prev, [name]: value }));
    };

    const handleUpdateSubmit = async () => {
        try {
            await updateDoc(doc(db, 'providers', currentUser.uid), updateData);
            setProviderData(updateData);
            setOpenUpdate(false);
        } catch (error) {
            alert("Update failed: " + error.message);
        }
    };

    return (
        <>
            <div className='flex-row'>
                <div className='card-type1 account-card'>
                    <div className='flex-column-stretch eightyperc-container'>
                        <div className='flex-row'>
                            <div className='circle size100px'>
                                <Person style={globalIconStyle} />
                            </div>
                        </div>
                        <h3 className='heading-type3 center-text'>{providerData.providerDataCompanyName}</h3>
                        <div className='flex-row'><p className='para-type2'>Account Manager:</p><p className='grey-container'>{providerData.providerDataFirstName} {providerData.providerDataLastName}</p></div>
                        <div className='flex-row'><p className='para-type2'>Email:</p><p className='grey-container'>{providerData.providerDataEmail}</p></div>
                        <div className='flex-row'><p className='para-type2'>Phone Number:</p><p className='grey-container'>{providerData.providerDataNumber}</p></div>
                        <div className='flex-row left-justify'><p className='para-type2'>About Your Company:</p></div>
                        <div className="flex-row"><p className='grey-container'>{providerData.providerDataDescription}</p></div>
                        <button className='button-type1' onClick={onSignOutSubmit}><Logout />Log Out</button>
                        <button className='button-type2' onClick={onDeleteUser}><Delete />Delete Account</button>
                        <button className='button-type1' onClick={() => setOpenUpdate(true)}>Update Details</button>
                    </div>
                </div>
            </div>
            <div className='card-type1 account-card'>
                <div className='flex-column-stretch ninetyfiveperc-container'>
                    <h4 className='heading-type3'>Your Services: {(providerData?.providerDataServices || []).length}</h4>
                    <div className="flex-column-stretch dark-grey-container">
                        {providerData?.providerDataServices?.length > 0 ? providerData.providerDataServices.map((eachService, index) => (
                            <ServiceCard2
                                key={`service-${eachService.serviceId || index}`}
                                passedService={eachService}
                                passedIndex={index}
                                showDelete={true}
                                onDeleteItem={onDeleteService}  // Updated prop to match what ServiceCard2 expects
                                onEditService={onEditService}
                            />
                        )) : <p className='para-type2'>No Services Added</p>}
                    </div>
                </div>
            </div>
            <div className='card-type1 account-card auto-side-margin'>
                <form onSubmit={onAddServiceSubmit} className='eightyperc-container'>
                    <h4 className='heading-type3'>{serviceInput.serviceId ? "Edit Service" : "Add a New Service"}</h4>
                    {/* NEW: Service Category dropdown shown first */}
                    <FormControl fullWidth sx={{ mb: 2 }}>
                        <Select
                            name='serviceCategory'
                            value={String(serviceInput.serviceCategory)}
                            onChange={(e) => setServiceInput(prev => ({ ...prev, serviceCategory: e.target.value }))}
                        >
                            <MenuItem value=''>Select Service Category</MenuItem>
                            <MenuItem value='Cleaning'>Cleaning</MenuItem>
                            <MenuItem value='Electricians'>Electricians</MenuItem>
                            <MenuItem value='Plumbers'>Plumbers</MenuItem>
                            <MenuItem value='Carpenters'>Carpenters</MenuItem>
                            <MenuItem value='Pest Control'>Pest Control</MenuItem>
                            <MenuItem value='Salon for Women'>Salon for Women</MenuItem>
                            <MenuItem value='Salon for Men'>Salon for Men</MenuItem>
                            <MenuItem value='Massage'>Massage</MenuItem>
                            <MenuItem value='Miscellanious'>Miscellanious</MenuItem>
                        </Select>
                    </FormControl>
                    {/* Then the rest of the service fields */}
                    <TextField
                        label='Service Title'
                        name='serviceTitle'
                        fullWidth
                        value={serviceInput.serviceTitle}
                        onChange={(e) => setServiceInput(prev => ({ ...prev, serviceTitle: e.target.value }))}
                        sx={{ mb: 2 }}
                    />
                    <TextField
                        label='Service Description'
                        name='serviceDescription'
                        fullWidth
                        multiline
                        rows={3}
                        value={serviceInput.serviceDescription}
                        onChange={(e) => setServiceInput(prev => ({ ...prev, serviceDescription: e.target.value }))}
                        sx={{ mb: 2 }}
                    />
                    <TextField
                        label='Service Price (Rs.)'
                        name='servicePrice'
                        fullWidth
                        type='number'
                        value={serviceInput.servicePrice}
                        onChange={(e) => setServiceInput(prev => ({ ...prev, servicePrice: e.target.value }))}
                        sx={{ mb: 2 }}
                    />
                    <MuiButton type='submit' variant='contained' fullWidth disabled={loading}>
                        {serviceInput.serviceId ? "Update Service" : "Submit"}
                    </MuiButton>
                </form>
            </div>
            <Dialog open={openUpdate} onClose={() => setOpenUpdate(false)}>
                <DialogTitle>Update Your Details</DialogTitle>
                <DialogContent>
                    <TextField label='First Name' name='providerDataFirstName' fullWidth value={updateData.providerDataFirstName} onChange={handleUpdateChange} />
                </DialogContent>
                <DialogActions>
                    <MuiButton onClick={() => setOpenUpdate(false)}>Cancel</MuiButton>
                    <MuiButton onClick={handleUpdateSubmit} variant='contained'>Update</MuiButton>
                </DialogActions>
            </Dialog>
        </>
    );
};

export default ProviderAccount;
