import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { doc, getDoc, updateDoc } from '@firebase/firestore';
import { db, storage } from '../firebaseInit';
import { ref, uploadBytes, getDownloadURL } from '@firebase/storage';
import ServiceCard2 from '../components/ServiceCard2';
import { Checkbox, FormControl, Select, MenuItem, InputLabel, Modal, Box, TextField, Button } from '@mui/material';
import { Person, Logout, Delete, Edit } from '@mui/icons-material';
import { globalIconStyle } from '../assets/GlobalStyles';

const ProviderAccount = ({ onSignOutSubmit, onDeleteUser }) => {
    const { currentUser } = useContext(AuthContext);
    const [providerData, setProviderData] = useState(null);
    const [loading, setLoading] = useState(false);
    const [editModalOpen, setEditModalOpen] = useState(false);
    const [updatedProviderData, setUpdatedProviderData] = useState({});
    const [serviceInput, setServiceInput] = useState({
        serviceTitle: '',
        serviceDescription: '',
        serviceCategory: '',
        servicePrice: 0.0,
        serviceAvailable: true,
        serviceImageName: '',
        serviceImageURL: '',
    });
    const [finalServiceImageInput, setFinalServiceImageInput] = useState(null);

    const serviceCategories = [
        'Cleaning', 'Electricians', 'Plumbers', 'Carpenters', 'Pest Control',
        'Salon for Women', 'Salon for Men', 'Miscellaneous'
    ];

    useEffect(() => {
        const fetchProviderData = async () => {
            if (!currentUser) return;

            const providerRef = doc(db, 'providers', currentUser.uid);
            const providerSnap = await getDoc(providerRef);

            if (providerSnap.exists()) {
                setProviderData(providerSnap.data());
            } else {
                console.log("No provider data found");
                setProviderData({ providerServices: [] });
            }
        };

        fetchProviderData();
    }, [currentUser]);

    const handleEditProfile = () => {
        setUpdatedProviderData(providerData);
        setEditModalOpen(true);
    };

    const handleInputChange = (e) => {
        setUpdatedProviderData({ ...updatedProviderData, [e.target.name]: e.target.value });
    };

    const handleSaveChanges = async () => {
        setLoading(true);
        try {
            await updateDoc(doc(db, 'providers', currentUser.uid), updatedProviderData);
            setProviderData(updatedProviderData);
            setEditModalOpen(false);
        } catch (error) {
            console.error("Error updating profile:", error.message);
        }
        setLoading(false);
    };

    return (
        <>
            <div className='flex-row'>
                <div className='card-type1 account-card'>
                    <div className='flex-column-stretch eightyperc-container'>
                        <div className='flex-row'>
                            <div className='circle size100px'>
                                <Person style={globalIconStyle} />
                            </div>
                        </div>
                        <h3 className='heading-type3 center-text'>{providerData?.providerCompanyName || 'Loading...'}</h3>
                        <p>Email: {providerData?.providerEmail || 'N/A'}</p>
                        <p>Phone Number: {providerData?.providerPhoneNumber || 'N/A'}</p>
                        <button className='button-type1' onClick={onSignOutSubmit}><Logout /> Log Out</button>
                        <button className='button-type2' onClick={onDeleteUser}><Delete /> Delete Account</button>
                        <button className='button-type3' onClick={handleEditProfile}><Edit /> Edit Profile</button>
                    </div>
                </div>

                <div className='card-type1 account-card'>
                    <h4 className='heading-type3'>Your Services: {providerData?.providerServices?.length || 0}</h4>
                    <div className="flex-column-stretch dark-grey-container">
                        {providerData?.providerServices?.length > 0 ? (
                            providerData.providerServices.map((service, index) => (
                                <ServiceCard2 key={index} passedService={service} passedIndex={index} onDeleteItem={() => { }} showDelete={false} />
                            ))
                        ) : (
                            <p>No Services Added</p>
                        )}
                    </div>
                </div>
            </div>

            <div className='card-type1 account-card auto-side-margin'>
                <form onSubmit={onAddServiceSubmit} className='eightyperc-container'>
                    <h4 className='heading-type3'>Add a New Service</h4>
                    <input type='text' name='serviceTitle' placeholder='Service Title' value={serviceInput.serviceTitle} onChange={handleServiceInputChange} required />
                    <textarea name='serviceDescription' placeholder='Service Description' value={serviceInput.serviceDescription} onChange={handleServiceInputChange} required />
                    <input type='number' name='servicePrice' placeholder='Price' value={serviceInput.servicePrice} onChange={handleServiceInputChange} required />

                    <FormControl variant="filled" fullWidth>
                        <InputLabel>Service Category</InputLabel>
                        <Select name='serviceCategory' value={serviceInput.serviceCategory} onChange={handleServiceInputChange} required>
                            {serviceCategories.map((category, index) => (
                                <MenuItem key={index} value={category}>{category}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <input type='file' onChange={handleServiceImageInputChange} />
                    <Checkbox name='serviceAvailable' checked={serviceInput.serviceAvailable} onChange={(e) => setServiceInput({ ...serviceInput, serviceAvailable: e.target.checked })} />
                    <input type='submit' className='button' value='Submit' disabled={loading} />
                </form>
            </div>

            <Modal open={editModalOpen} onClose={() => setEditModalOpen(false)}>
                <Box sx={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 400, bgcolor: 'background.paper', boxShadow: 24, p: 4 }}>
                    <h3>Edit Profile</h3>
                    <TextField fullWidth label="Company Name" name="providerCompanyName" value={updatedProviderData.providerCompanyName || ''} onChange={handleInputChange} margin="normal" />
                    <TextField fullWidth label="Email" name="providerEmail" value={updatedProviderData.providerEmail || ''} onChange={handleInputChange} margin="normal" />
                    <TextField fullWidth label="Phone Number" name="providerPhoneNumber" value={updatedProviderData.providerPhoneNumber || ''} onChange={handleInputChange} margin="normal" />
                    <Button variant="contained" color="primary" onClick={handleSaveChanges} disabled={loading}>Save Changes</Button>
                </Box>
            </Modal>
        </>
    );
};

export default ProviderAccount;
