import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import {
    createUserWithEmailAndPassword,
    signInWithPopup,
    sendEmailVerification,
    RecaptchaVerifier,
    signInWithPhoneNumber
} from 'firebase/auth';
import { auth, googleProvider, db } from '../firebaseInit';
import { doc, setDoc } from 'firebase/firestore';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import { Button } from '@mui/material';
import Loading from '../components/Loading';

const UserSignUpForm = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [userSignUpState, setUserSignUpState] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        phoneNumber: '',
        otp: ''
    });
    const [otpSent, setOtpSent] = useState(false);
    const [confirmationResult, setConfirmationResult] = useState(null);

    const handleChange = (e) => {
        setUserSignUpState({
            ...userSignUpState,
            [e.target.name]: e.target.value
        });
    };

    const handleSignUp = async (e) => {
        e.preventDefault();
        if (userSignUpState.password !== userSignUpState.confirmPassword) {
            alert('Passwords do not match!');
            return;
        }
        setLoading(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, userSignUpState.email, userSignUpState.password);
            const user = userCredential.user;

            // Send email verification
            await sendEmailVerification(user);
            alert('A verification email has been sent. Please check your inbox and verify your email before logging in.');

            // Store user data in Firestore with "verified: false"
            await setDoc(doc(db, 'users', user.uid), {
                name: userSignUpState.name,
                email: userSignUpState.email,
                phoneNumber: userSignUpState.phoneNumber,
                verified: false, // Mark as not verified initially
            });

            setUserSignUpState({
                name: '',
                email: '',
                password: '',
                confirmPassword: '',
                phoneNumber: '',
                otp: ''
            });

        } catch (error) {
            alert(`Error: ${error.message}`);
        }
        setLoading(false);
    };

    const handleGoogleSignUp = async () => {
        try {
            setLoading(true);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;

            if (!user.emailVerified) {
                // Send email verification if not already verified
                await sendEmailVerification(user);
                alert('A verification email has been sent. Please verify your email before proceeding.');
                return;
            }

            await setDoc(doc(db, 'users', user.uid), {
                name: user.displayName,
                email: user.email,
                phoneNumber: user.phoneNumber || '',
                verified: true // Google users are already verified
            }, { merge: true });

            alert('Google Sign-In Successful!');
            history.push('/');
        } catch (error) {
            alert(`Google Sign-In Failed: ${error.message}`);
        }
        setLoading(false);
    };

    const sendOtp = async () => {
        window.recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
            size: 'invisible'
        });
        try {
            const confirmation = await signInWithPhoneNumber(auth, `+${userSignUpState.phoneNumber}`, window.recaptchaVerifier);
            setConfirmationResult(confirmation);
            setOtpSent(true);
            alert('OTP sent to phone!');
        } catch (error) {
            alert(`Failed to send OTP: ${error.message}`);
        }
    };

    const verifyOtp = async () => {
        try {
            const result = await confirmationResult.confirm(userSignUpState.otp);
            alert('Phone authentication successful!');
            history.push('/');
        } catch (error) {
            alert(`Invalid OTP: ${error.message}`);
        }
    };

    return (
        <div>
            {loading ? <Loading /> : (
                <form onSubmit={handleSignUp}>
                    <h2>User Sign Up</h2>
                    <input type='text' name='name' placeholder='Full Name' value={userSignUpState.name} onChange={handleChange} required />
                    <input type='email' name='email' placeholder='Email' value={userSignUpState.email} onChange={handleChange} required />
                    <input type='password' name='password' placeholder='Password' value={userSignUpState.password} onChange={handleChange} required />
                    <input type='password' name='confirmPassword' placeholder='Confirm Password' value={userSignUpState.confirmPassword} onChange={handleChange} required />
                    <PhoneInput country={'in'} value={userSignUpState.phoneNumber} onChange={(phone) => setUserSignUpState({ ...userSignUpState, phoneNumber: phone })} />
                    <Button onClick={sendOtp}>Send OTP</Button>
                    {otpSent && (
                        <>
                            <input type='text' name='otp' placeholder='Enter OTP' value={userSignUpState.otp} onChange={handleChange} required />
                            <Button onClick={verifyOtp}>Verify OTP</Button>
                        </>
                    )}
                    <Button type='submit'>Sign Up</Button>
                    <Button onClick={handleGoogleSignUp}>Sign Up with Google</Button>
                    <div id='recaptcha-container'></div>
                </form>
            )}
        </div>
    );
};

export default UserSignUpForm;
