import React, { useContext, useState, useEffect, useCallback } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { doc, collection, query, getDoc, updateDoc, onSnapshot } from 'firebase/firestore';
import { db } from '../firebaseInit';
import Categories from '../components/Categories';
import ServiceCard from '../components/ServiceCard';
import Loading from '../components/Loading';

const Services = () => {
    const { currentUser, isProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [services, setServices] = useState({});
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        cart: []
    });

    const serviceCategories = [
        'Cleaning', 'Electricians', 'Plumbers', 'Carpenters', 'Pest Control',
        'Salon for Women', 'Salon for Men', 'Miscellaneous'
    ];

    const readUserData = useCallback(async () => {
        if (currentUser) {
            const docSnap = await getDoc(doc(db, 'users', currentUser.uid));
            if (docSnap.exists()) {
                const data = docSnap.data();
                setUserData({
                    name: data.name,
                    email: data.email,
                    cart: data.cart || []
                });
            }
        }
    }, [currentUser]);

    useEffect(() => {
        const fetchData = async () => {
            await readUserData();
        };
        fetchData();

        const q = query(collection(db, 'providers'));
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            let allServices = [];
            querySnapshot.forEach((doc) => {
                const providerData = doc.data();
                if (providerData.providerServices) {
                    allServices = [...allServices, ...providerData.providerServices];
                }
            });

            const categorizedServices = {};
            serviceCategories.forEach(category => {
                categorizedServices[category] = allServices.filter(service => service.serviceCategory === category);
            });

            setServices(categorizedServices);
            setLoading(false);
        });

        return () => unsubscribe();
    }, [readUserData, serviceCategories]);

    const onAddToCart = async (category, index) => {
        setLoading(true);
        if (!currentUser) {
            alert('Please login or sign up first.');
            setLoading(false);
            return;
        }

        const selectedService = services[category][index];
        const updatedCart = [...userData.cart, selectedService];

        setUserData(prev => ({ ...prev, cart: updatedCart }));
        await updateDoc(doc(db, 'users', currentUser.uid), { cart: updatedCart })
            .catch(error => console.error('Error adding to cart:', error));

        alert(`Added ${selectedService.serviceTitle} by ${selectedService.serviceProvider} to cart.`);
        setLoading(false);
    };

    return (
        <div className='eightyperc-container'>
            <div className='card-type1'>
                <div className='ninetyfiveperc-container'>
                    <h2 className='heading-type3'>Services</h2>
                    <p>Choose from the best services at the lowest prices:</p>
                </div>
            </div>
            <Categories />

            {serviceCategories.map(category => (
                <div className='card-type1' id={category} key={category}>
                    <div className='ninetyfiveperc-container'>
                        <div className='services flex-column-stretch'>
                            <h3 className='para-type1'>{category}:</h3>
                            <div className='flex-column-stretch dark-grey-container'>
                                {loading ? <Loading /> : (
                                    services[category]?.length ? services[category].map((service, index) => (
                                        <ServiceCard
                                            key={index}
                                            passedService={service}
                                            passedIndex={index}
                                            showButton={!isProvider}
                                            onAddToCart={onAddToCart}
                                        />
                                    )) : <p className='para-type2'>No {category} Services</p>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Services;
