import React, { useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { auth, db } from '../firebaseInit';
import { createUserWithEmailAndPassword, signInWithPopup, RecaptchaVerifier, signInWithPhoneNumber } from 'firebase/auth';
import { GoogleAuthProvider } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import Loading from '../components/Loading';
import { globalIconStyle } from '../assets/GlobalStyles';
import { PersonAdd } from '@mui/icons-material';

const ProviderSignUpForm = () => {
    const history = useHistory();
    const { setIsProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [otpSent, setOtpSent] = useState(false);
    const [verificationId, setVerificationId] = useState(null);
    const [otp, setOtp] = useState('');
    const [providerSignUpState, setProviderSignUpState] = useState({
        name: '',
        companyName: '',
        email: '',
        phone: '',
        description: '',
        password: '',
        confirmPassword: ''
    });

    const handleChange = (e) => {
        setProviderSignUpState({
            ...providerSignUpState,
            [e.target.name]: e.target.value
        });
    };

    const handleGoogleSignIn = async () => {
        const provider = new GoogleAuthProvider();
        try {
            const result = await signInWithPopup(auth, provider);
            const user = result.user;
            await setDoc(doc(db, 'providers', user.uid), {
                name: user.displayName,
                email: user.email,
                phone: user.phoneNumber || 'Not Provided',
                companyName: '',
                description: '',
                role: 1
            });
            history.push('/');
        } catch (error) {
            alert(`Google Sign-In failed: ${error.message}`);
        }
    };

    const handlePhoneSignIn = async () => {
        if (!providerSignUpState.phone) {
            alert('Please enter a valid phone number');
            return;
        }

        try {
            const recaptcha = new RecaptchaVerifier(auth, 'recaptcha-container', {});
            const confirmation = await signInWithPhoneNumber(auth, providerSignUpState.phone, recaptcha);
            setVerificationId(confirmation);
            setOtpSent(true);
        } catch (error) {
            alert(`Phone authentication failed: ${error.message}`);
        }
    };

    const verifyOtp = async () => {
        if (!otp) {
            alert('Please enter the OTP');
            return;
        }
        try {
            const result = await verificationId.confirm(otp);
            const user = result.user;
            await setDoc(doc(db, 'providers', user.uid), {
                name: providerSignUpState.name,
                email: user.email || 'Not Provided',
                phone: user.phoneNumber,
                companyName: providerSignUpState.companyName,
                description: providerSignUpState.description,
                role: 1
            });
            history.push('/');
        } catch (error) {
            alert(`OTP verification failed: ${error.message}`);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (providerSignUpState.password !== providerSignUpState.confirmPassword) {
            alert('Passwords do not match');
            return;
        }

        setLoading(true);
        setIsProvider(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, providerSignUpState.email, providerSignUpState.password);
            const user = userCredential.user;
            await setDoc(doc(db, 'providers', user.uid), {
                name: providerSignUpState.name,
                email: providerSignUpState.email,
                phone: providerSignUpState.phone,
                companyName: providerSignUpState.companyName,
                description: providerSignUpState.description,
                role: 1
            });
            history.push('/');
        } catch (error) {
            alert(`Error signing up: ${error.message}`);
        }
        setLoading(false);
    };

    return (
        <div className='signup-container'>
            {loading && <Loading />}
            <h2>Provider Sign Up</h2>
            <form onSubmit={handleSubmit}>
                <input type='text' name='name' placeholder='Full Name' value={providerSignUpState.name} onChange={handleChange} required />
                <input type='text' name='companyName' placeholder='Company Name' value={providerSignUpState.companyName} onChange={handleChange} required />
                <input type='email' name='email' placeholder='Email' value={providerSignUpState.email} onChange={handleChange} required />
                <PhoneInput country={'us'} value={providerSignUpState.phone} onChange={(phone) => setProviderSignUpState({ ...providerSignUpState, phone })} />
                <textarea name='description' placeholder='Company Description' value={providerSignUpState.description} onChange={handleChange} required />
                <input type='password' name='password' placeholder='Password' value={providerSignUpState.password} onChange={handleChange} required />
                <input type='password' name='confirmPassword' placeholder='Confirm Password' value={providerSignUpState.confirmPassword} onChange={handleChange} required />
                <button type='submit' disabled={loading}>Sign Up</button>
            </form>
            <button onClick={handleGoogleSignIn}>Sign Up with Google</button>
            {!otpSent ? (
                <button onClick={handlePhoneSignIn}>Sign Up with Phone</button>
            ) : (
                <div>
                    <input type='text' placeholder='Enter OTP' value={otp} onChange={(e) => setOtp(e.target.value)} />
                    <button onClick={verifyOtp}>Verify OTP</button>
                </div>
            )}
            <div id='recaptcha-container'></div>
        </div>
    );
};

export default ProviderSignUpForm;
