import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { collection, query, onSnapshot, doc, getDoc, updateDoc } from 'firebase/firestore'; // Added doc and getDoc here
import { db } from '../firebaseInit';
import Categories from '../components/Categories';
import ServiceCard from '../components/ServiceCard';
import Loading from '../components/Loading';

const Services = () => {
    const { currentUser, isProvider } = useContext(AuthContext);
    const [loading, setLoading] = useState(false);
    const [allServices, setAllServices] = useState([]);
    const [userData, setUserData] = useState({
        userDataFirstName: '',
        userDataLastName: '',
        userDataEmail: '',
        userDataCart: []
    });

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(() => {
        setLoading(true);
        const q = query(collection(db, 'providers'));
        const unsubscribe = onSnapshot(q, (snapshot) => {
            let services = [];
            snapshot.forEach((doc) => {
                const data = doc.data();
                // Ensure providerServices exists and is an array
                services = services.concat(data.providerServices || []);
            });
            // Filter and update services state:
            setAllServices(services.filter(service => service !== undefined && service !== null));
            setLoading(false);
        }, (error) => {
            console.error("Error listening for services: ", error);
            setLoading(false);
        });
        readUserData();
        return () => unsubscribe();
    }, []);

    const readUserData = async () => {
        if (currentUser != null) {
            var docSnap = await getDoc(doc(db, 'users', currentUser.uid));
            if (docSnap.exists()) {
                console.log(`Fetched user data:`, docSnap.data());
                setUserData({
                    ...userData,
                    userDataFirstName: docSnap.data().userFirstName,
                    userDataLastName: docSnap.data().userLastName,
                    userDataEmail: docSnap.data().userEmail,
                    userDataCart: docSnap.data().userCart
                });
            } else {
                console.log(`Not signed in by user.`);
            }
        }
    };

    const onAddToCart = async (passedCategory, passedIndex) => {
        setLoading(true);
        if (currentUser != null) {
            const whichServices = allServices.filter(service => service.serviceCategory === passedCategory);
            const addedService = whichServices[passedIndex];
            const finalCart = [...userData.userDataCart, addedService];
            setUserData(userData => ({
                ...userData,
                userDataCart: finalCart
            }));
            await updateDoc(doc(db, 'users', currentUser.uid), {
                userCart: finalCart
            }).catch((error) => {
                console.log(`in services/addToCart/fireStore_upDoc: Error Code ${error.code}: ${error.message}`);
                setLoading(false);
                return;
            });
            alert(`Added ${addedService.serviceTitle} by ${addedService.serviceProvider} to cart.`);
        } else {
            alert(`Please Login or Signup first.`);
        }
        setLoading(false);
    };

    /* Example filtering for a specific category: */
    const validCleaningServices = allServices.filter(service => service.serviceCategory === 'Cleaning');
    const validElectricianServices = allServices.filter(service => service.serviceCategory === 'Electricians');
