import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import UserSignUpForm from '../components/UserSignUpForm.js';
import ProviderSignUpForm from '../components/ProviderSignUpForm';
import { Switch as MaterialSwitch } from '@mui/material';

const SignUp = () => {
    const { isProvider, setIsProvider } = useContext(AuthContext);
    const [showProviderForm, setShowProviderForm] = useState(false);

    const handleSwitchCheck = (e) => {
        setShowProviderForm(e.target.checked);
        setIsProvider(e.target.checked);
    };

    return (
        <div className='eightyperc-container'>
            <div className='card-type1 signup-card signup-card2'>
                <div className='flex-row'>
                    <p>Sign up as a Service Provider?</p>
                    <MaterialSwitch onChange={handleSwitchCheck} color='primary' checked={showProviderForm} />
                </div>
            </div>
            {showProviderForm ? <ProviderSignUpForm /> : <UserSignUpForm />}
            <div className='card-type1 signup-card signup-card2'>
                <p>Existing user? <a href='/login' className='blue-link'>Log in</a> instead.</p>
            </div>
        </div>
    );
};

export default SignUp;
